import React, { useState, useEffect, useRef } from 'react';
import { Play, Pause, SkipBack, Download, RotateCcw } from 'lucide-react';

const RouteComparisonVisualizer = () => {
  const [allPlays, setAllPlays] = useState([]);
  const [routeTypes, setRouteTypes] = useState([]);
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [routePlays, setRoutePlays] = useState([]);
  const [selectedPlays, setSelectedPlays] = useState([]);
  const [currentFrame, setCurrentFrame] = useState(0);
  const [maxFrames, setMaxFrames] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [playSpeed, setPlaySpeed] = useState(80);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const canvasRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const recordedChunksRef = useRef([]);

  // Color palette for different plays
  const playColors = [
    { main: '#3b82f6', light: '#93c5fd', name: 'Blue' },
    { main: '#10b981', light: '#6ee7b7', name: 'Green' },
    { main: '#f59e0b', light: '#fcd34d', name: 'Orange' },
    { main: '#ef4444', light: '#fca5a5', name: 'Red' },
    { main: '#8b5cf6', light: '#c4b5fd', name: 'Purple' },
    { main: '#ec4899', light: '#f9a8d4', name: 'Pink' },
  ];

  const parseCSV = (text) => {
    const lines = text.split('\n');
    const headers = lines[0].split(',').map(h => h.trim());

    return lines.slice(1)
      .filter(line => line.trim())
      .map(line => {
        const values = line.split(',');
        const obj = {};
        headers.forEach((header, i) => {
          obj[header] = values[i]?.trim();
        });
        return obj;
      });
  };

  const parseCSVData = (inputData, outputData, suppData) => {
    const playMap = new Map();

    inputData.forEach(row => {
      const key = `${row.game_id}_${row.play_id}`;
      if (!playMap.has(key)) {
        playMap.set(key, {
          gameId: row.game_id,
          playId: row.play_id,
          inputFrames: [],
          outputFrames: [],
          playInfo: null
        });
      }
      playMap.get(key).inputFrames.push(row);
    });

    outputData.forEach(row => {
      const key = `${row.game_id}_${row.play_id}`;
      if (playMap.has(key)) {
        playMap.get(key).outputFrames.push(row);
      }
    });

    suppData.forEach(row => {
      const key = `${row.game_id}_${row.play_id}`;
      if (playMap.has(key)) {
        playMap.get(key).playInfo = row;
      }
    });

    const plays = [];
    const routeSet = new Set();

    playMap.forEach((playData) => {
      const allFrames = [
        ...playData.inputFrames.map(f => ({ ...f, source: 'input' })),
        ...playData.outputFrames.map(f => ({ ...f, source: 'output' }))
      ].sort((a, b) => parseFloat(a.frame_id) - parseFloat(b.frame_id));

      const frameMap = new Map();
      allFrames.forEach(row => {
        const frameId = parseFloat(row.frame_id);
        if (!frameMap.has(frameId)) {
          frameMap.set(frameId, {
            frameId: frameId,
            players: [],
            separation: 0
          });
        }

        const frame = frameMap.get(frameId);

        if (row.player_role) {
          frame.players.push({
            x: parseFloat(row.x),
            y: parseFloat(row.y),
            role: row.player_role,
            name: row.player_name || row.display_name || '',
            speed: parseFloat(row.s) || 0
          });
        }
      });

      frameMap.forEach((frame) => {
        const wr = frame.players.find(p => p.role === 'Targeted Receiver');
        const defenders = frame.players.filter(p => p.role === 'Defensive Coverage');

        if (wr && defenders.length > 0) {
          const distances = defenders.map(def => 
            Math.sqrt(Math.pow(wr.x - def.x, 2) + Math.pow(wr.y - def.y, 2))
          );
          frame.separation = Math.min(...distances);
        }
      });

      const frames = Array.from(frameMap.values());

      if (frames.length > 0 && playData.playInfo?.route_of_targeted_receiver) {
        const route = playData.playInfo.route_of_targeted_receiver;
        routeSet.add(route);

        const wr = frames[0].players.find(p => p.role === 'Targeted Receiver');
        const separationData = frames.map(f => ({
          time: f.frameId / 10,
          separation: f.separation,
          wrSpeed: f.players.find(p => p.role === 'Targeted Receiver')?.speed || 0
        }));

        const maxSep = Math.max(...separationData.map(d => d.separation));
        const maxSepTime = separationData.find(d => d.separation === maxSep)?.time || 0;

        plays.push({
          gameId: playData.gameId,
          playId: playData.playId,
          route: route,
          passResult: playData.playInfo?.pass_result || 'N/A',
          wrName: wr?.name || 'Unknown',
          frames: frames,
          separationData: separationData,
          maxSeparation: maxSep,
          maxSeparationTime: maxSepTime,
          avgSeparation: separationData.reduce((sum, d) => sum + d.separation, 0) / separationData.length,
          initialSeparation: separationData[0]?.separation || 0
        });
      }
    });

    return { plays, routes: Array.from(routeSet).sort() };
  };

  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);

        const [inputRes, outputRes, suppRes] = await Promise.all([
          fetch('/data/input_all.csv'),
          fetch('/data/output_all.csv'),
          fetch('/data/supplementary_data.csv')
        ]);

        if (!inputRes.ok || !outputRes.ok || !suppRes.ok) {
          throw new Error('Failed to load data files');
        }

        const [inputText, outputText, suppText] = await Promise.all([
          inputRes.text(),
          outputRes.text(),
          suppRes.text()
        ]);

        const inputData = parseCSV(inputText);
        const outputData = parseCSV(outputText);
        const suppData = parseCSV(suppText);

        const { plays, routes } = parseCSVData(inputData, outputData, suppData);

        setAllPlays(plays);
        setRouteTypes(routes);

        if (routes.length > 0) {
          setSelectedRoute(routes[0]);
        }

        setLoading(false);
      } catch (err) {
        console.error('Error loading data:', err);
        setError(err.message);
        setLoading(false);
      }
    };

    loadData();
  }, []);

  useEffect(() => {
    if (selectedRoute && allPlays.length > 0) {
      const plays = allPlays.filter(p => p.route === selectedRoute);
      setRoutePlays(plays);
      // Auto-select first 3 plays (or all if less than 3)
      const autoSelect = plays.slice(0, Math.min(3, plays.length));
      setSelectedPlays(autoSelect);
    }
  }, [selectedRoute, allPlays]);

  useEffect(() => {
    if (selectedPlays.length > 0) {
      const max = Math.max(...selectedPlays.map(p => p.separationData.length));
      setMaxFrames(max);
      setCurrentFrame(0);
    }
  }, [selectedPlays]);

  useEffect(() => {
    if (!isPlaying || selectedPlays.length === 0) return;

    const interval = setInterval(() => {
      setCurrentFrame(prev => {
        if (prev >= maxFrames - 1) {
          setIsPlaying(false);
          return prev;
        }
        return prev + 1;
      });
    }, playSpeed);

    return () => clearInterval(interval);
  }, [isPlaying, maxFrames, playSpeed]);

  useEffect(() => {
    if (selectedPlays.length === 0 || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const width = canvas.width;
    const height = canvas.height;

    // Background
    ctx.fillStyle = '#111827';
    ctx.fillRect(0, 0, width, height);

    // Chart area dimensions
    const chartLeft = 80;
    const chartRight = width - 250;
    const chartTop = 100;
    const chartBottom = height - 80;
    const chartWidth = chartRight - chartLeft;
    const chartHeight = chartBottom - chartTop;

    // Calculate scales
    const maxTime = Math.max(...selectedPlays.flatMap(p => p.separationData.map(d => d.time)));
    const maxSep = Math.max(...selectedPlays.flatMap(p => p.separationData.map(d => d.separation))) * 1.1;

    // Draw title
    ctx.fillStyle = 'white';
    ctx.font = 'bold 24px sans-serif';
    ctx.textAlign = 'center';
    ctx.fillText(`Route Comparison: ${selectedRoute}`, width / 2, 40);

    ctx.fillStyle = '#9ca3af';
    ctx.font = '14px sans-serif';
    ctx.fillText(`Separation over time - ${selectedPlays.length} plays analyzed`, width / 2, 65);

    // Draw chart background
    ctx.fillStyle = '#1f2937';
    ctx.fillRect(chartLeft, chartTop, chartWidth, chartHeight);

    // Draw grid lines
    ctx.strokeStyle = '#374151';
    ctx.lineWidth = 1;

    // Horizontal grid lines (separation)
    for (let i = 0; i <= 5; i++) {
      const y = chartTop + (chartHeight * i / 5);
      ctx.beginPath();
      ctx.moveTo(chartLeft, y);
      ctx.lineTo(chartRight, y);
      ctx.stroke();

      // Y-axis labels
      const sepValue = maxSep * (1 - i / 5);
      ctx.fillStyle = '#9ca3af';
      ctx.font = '11px sans-serif';
      ctx.textAlign = 'right';
      ctx.fillText(`${sepValue.toFixed(1)} yds`, chartLeft - 10, y + 4);
    }

    // Vertical grid lines (time)
    const timeStep = Math.ceil(maxTime);
    for (let i = 0; i <= timeStep; i++) {
      const x = chartLeft + (chartWidth * i / timeStep);
      ctx.beginPath();
      ctx.moveTo(x, chartTop);
      ctx.lineTo(x, chartBottom);
      ctx.stroke();

      // X-axis labels
      ctx.fillStyle = '#9ca3af';
      ctx.font = '11px sans-serif';
      ctx.textAlign = 'center';
      ctx.fillText(`${i}s`, x, chartBottom + 20);
    }

    // Axis labels
    ctx.fillStyle = 'white';
    ctx.font = 'bold 12px sans-serif';
    ctx.textAlign = 'center';
    ctx.fillText('Time (seconds)', (chartLeft + chartRight) / 2, chartBottom + 45);

    ctx.save();
    ctx.translate(25, (chartTop + chartBottom) / 2);
    ctx.rotate(-Math.PI / 2);
    ctx.fillText('Separation (yards)', 0, 0);
    ctx.restore();

    // Current time indicator
    const currentTime = selectedPlays[0]?.separationData[currentFrame]?.time || 0;
    const timeX = chartLeft + (currentTime / maxTime) * chartWidth;

    ctx.strokeStyle = 'rgba(255, 255, 255, 0.5)';
    ctx.lineWidth = 2;
    ctx.setLineDash([5, 5]);
    ctx.beginPath();
    ctx.moveTo(timeX, chartTop);
    ctx.lineTo(timeX, chartBottom);
    ctx.stroke();
    ctx.setLineDash([]);

    // Draw separation curves for each selected play
    selectedPlays.forEach((play, playIndex) => {
      const color = playColors[playIndex % playColors.length];

      // Draw full curve (faded)
      ctx.strokeStyle = `${color.main}40`;
      ctx.lineWidth = 2;
      ctx.beginPath();

      play.separationData.forEach((point, i) => {
        const x = chartLeft + (point.time / maxTime) * chartWidth;
        const y = chartBottom - (point.separation / maxSep) * chartHeight;

        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      });
      ctx.stroke();

      // Draw animated curve up to current frame
      ctx.strokeStyle = color.main;
      ctx.lineWidth = 3;
      ctx.beginPath();

      const framesToDraw = Math.min(currentFrame + 1, play.separationData.length);
      for (let i = 0; i < framesToDraw; i++) {
        const point = play.separationData[i];
        const x = chartLeft + (point.time / maxTime) * chartWidth;
        const y = chartBottom - (point.separation / maxSep) * chartHeight;

        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      }
      ctx.stroke();

      // Draw current point
      if (currentFrame < play.separationData.length) {
        const point = play.separationData[currentFrame];
        const x = chartLeft + (point.time / maxTime) * chartWidth;
        const y = chartBottom - (point.separation / maxSep) * chartHeight;

        ctx.fillStyle = color.main;
        ctx.beginPath();
        ctx.arc(x, y, 6, 0, Math.PI * 2);
        ctx.fill();

        ctx.fillStyle = 'white';
        ctx.beginPath();
        ctx.arc(x, y, 3, 0, Math.PI * 2);
        ctx.fill();
      }

      // Mark max separation point
      const maxPoint = play.separationData.find(d => d.separation === play.maxSeparation);
      if (maxPoint) {
        const maxX = chartLeft + (maxPoint.time / maxTime) * chartWidth;
        const maxY = chartBottom - (maxPoint.separation / maxSep) * chartHeight;

        ctx.strokeStyle = color.light;
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.arc(maxX, maxY, 10, 0, Math.PI * 2);
        ctx.stroke();
      }
    });

    // Legend panel (right side)
    const legendX = chartRight + 20;
    const legendY = chartTop;
    const legendWidth = 210;
    const legendItemHeight = 85;

    ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
    ctx.fillRect(legendX, legendY, legendWidth, selectedPlays.length * legendItemHeight + 20);
    ctx.strokeStyle = '#4b5563';
    ctx.lineWidth = 1;
    ctx.strokeRect(legendX, legendY, legendWidth, selectedPlays.length * legendItemHeight + 20);

    ctx.fillStyle = 'white';
    ctx.font = 'bold 12px sans-serif';
    ctx.textAlign = 'left';
    ctx.fillText('PLAYS', legendX + 10, legendY + 18);

    selectedPlays.forEach((play, index) => {
      const color = playColors[index % playColors.length];
      const itemY = legendY + 30 + index * legendItemHeight;

      // Color indicator
      ctx.fillStyle = color.main;
      ctx.fillRect(legendX + 10, itemY, 4, 70);

      // Play info
      ctx.fillStyle = color.main;
      ctx.font = 'bold 11px sans-serif';
      ctx.fillText(`Play ${play.playId}`, legendX + 22, itemY + 12);

      ctx.fillStyle = 'white';
      ctx.font = '10px sans-serif';
      ctx.fillText(play.wrName, legendX + 22, itemY + 26);

      // Result badge
      const resultColor = play.passResult === 'C' ? '#10b981' : '#ef4444';
      ctx.fillStyle = resultColor;
      ctx.font = 'bold 9px sans-serif';
      const resultText = play.passResult === 'C' ? 'COMPLETE' : 'INCOMPLETE';
      ctx.fillText(resultText, legendX + 22, itemY + 40);

      // Current separation
      const currentSep = play.separationData[Math.min(currentFrame, play.separationData.length - 1)]?.separation || 0;
      ctx.fillStyle = '#9ca3af';
      ctx.font = '10px sans-serif';
      ctx.fillText(`Current: ${currentSep.toFixed(1)} yds`, legendX + 22, itemY + 54);
      ctx.fillText(`Max: ${play.maxSeparation.toFixed(1)} @ ${play.maxSeparationTime.toFixed(1)}s`, legendX + 22, itemY + 68);
    });

    // Stats summary panel (bottom)
    const statsY = chartBottom + 55;
    ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
    ctx.fillRect(chartLeft, statsY, chartWidth, 50);

    // Calculate aggregate stats
    const avgMaxSep = selectedPlays.reduce((sum, p) => sum + p.maxSeparation, 0) / selectedPlays.length;
    const avgTimeToMax = selectedPlays.reduce((sum, p) => sum + p.maxSeparationTime, 0) / selectedPlays.length;
    const completionRate = selectedPlays.filter(p => p.passResult === 'C').length / selectedPlays.length * 100;

    const statsWidth = chartWidth / 4;

    ctx.textAlign = 'center';
    ctx.fillStyle = '#9ca3af';
    ctx.font = '10px sans-serif';
    ctx.fillText('AVG MAX SEP', chartLeft + statsWidth * 0.5, statsY + 15);
    ctx.fillStyle = '#3b82f6';
    ctx.font = 'bold 16px sans-serif';
    ctx.fillText(`${avgMaxSep.toFixed(1)} yds`, chartLeft + statsWidth * 0.5, statsY + 35);

    ctx.fillStyle = '#9ca3af';
    ctx.font = '10px sans-serif';
    ctx.fillText('AVG TIME TO MAX', chartLeft + statsWidth * 1.5, statsY + 15);
    ctx.fillStyle = '#10b981';
    ctx.font = 'bold 16px sans-serif';
    ctx.fillText(`${avgTimeToMax.toFixed(1)}s`, chartLeft + statsWidth * 1.5, statsY + 35);

    ctx.fillStyle = '#9ca3af';
    ctx.font = '10px sans-serif';
    ctx.fillText('COMPLETION RATE', chartLeft + statsWidth * 2.5, statsY + 15);
    ctx.fillStyle = '#fbbf24';
    ctx.font = 'bold 16px sans-serif';
    ctx.fillText(`${completionRate.toFixed(0)}%`, chartLeft + statsWidth * 2.5, statsY + 35);

    ctx.fillStyle = '#9ca3af';
    ctx.font = '10px sans-serif';
    ctx.fillText('CURRENT TIME', chartLeft + statsWidth * 3.5, statsY + 15);
    ctx.fillStyle = 'white';
    ctx.font = 'bold 16px sans-serif';
    ctx.fillText(`${currentTime.toFixed(1)}s`, chartLeft + statsWidth * 3.5, statsY + 35);

  }, [currentFrame, selectedPlays, selectedRoute, maxFrames]);

  const togglePlaySelection = (play) => {
    if (selectedPlays.find(p => p.playId === play.playId && p.gameId === play.gameId)) {
      setSelectedPlays(selectedPlays.filter(p => !(p.playId === play.playId && p.gameId === play.gameId)));
    } else if (selectedPlays.length < 6) {
      setSelectedPlays([...selectedPlays, play]);
    }
  };

  const handlePlayPause = () => setIsPlaying(!isPlaying);
  const handleReset = () => { setCurrentFrame(0); setIsPlaying(false); };

  const startRecording = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    recordedChunksRef.current = [];

    const stream = canvas.captureStream(30);
    const mediaRecorder = new MediaRecorder(stream, {
      mimeType: 'video/webm;codecs=vp9',
      videoBitsPerSecond: 3000000
    });

    mediaRecorder.ondataavailable = (event) => {
      if (event.data.size > 0) {
        recordedChunksRef.current.push(event.data);
      }
    };

    mediaRecorder.onstop = () => {
      const blob = new Blob(recordedChunksRef.current, { type: 'video/webm' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `route_comparison_${selectedRoute}_${selectedPlays.length}plays.webm`;
      a.click();
      URL.revokeObjectURL(url);
      setIsRecording(false);
    };

    mediaRecorder.start();
    mediaRecorderRef.current = mediaRecorder;
    setIsRecording(true);
    setIsPlaying(true);
    setCurrentFrame(0);
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsPlaying(false);
    }
  };

  useEffect(() => {
    if (isRecording && currentFrame >= maxFrames - 1) {
      setTimeout(() => stopRecording(), 500);
    }
  }, [currentFrame, isRecording, maxFrames]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-900 text-white">
        <div className="text-center">
          <div className="text-xl mb-2">Loading NFL tracking data...</div>
          <div className="text-sm text-gray-400">Analyzing route separation patterns</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-900 text-white">
        <div className="text-center max-w-md">
          <div className="text-xl mb-2 text-red-400">Error Loading Data</div>
          <div className="text-sm text-gray-400 mb-4">{error}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full min-h-screen bg-gray-900 text-white p-6">
      <div className="max-w-6xl mx-auto">
        <div className="mb-6">
          <h1 className="text-2xl font-bold mb-1">Route Separation Comparison</h1>
          <p className="text-sm text-gray-400">Compare separation patterns across multiple plays of the same route</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-4 mb-4">
          <div className="lg:col-span-1">
            <label className="block text-sm font-medium mb-2">Select Route Type</label>
            <select
              value={selectedRoute || ''}
              onChange={(e) => setSelectedRoute(e.target.value)}
              className="w-full bg-gray-800 border border-gray-700 rounded-lg px-4 py-2 text-white"
            >
              {routeTypes.map(route => (
                <option key={route} value={route}>
                  {route} ({allPlays.filter(p => p.route === route).length} plays)
                </option>
              ))}
            </select>
          </div>

          <div className="lg:col-span-3">
            <label className="block text-sm font-medium mb-2">
              Select Plays to Compare (max 6) - {selectedPlays.length} selected
            </label>
            <div className="flex flex-wrap gap-2 max-h-20 overflow-y-auto bg-gray-800 rounded-lg p-2">
              {routePlays.map((play, index) => {
                const isSelected = selectedPlays.find(p => p.playId === play.playId && p.gameId === play.gameId);
                const color = isSelected ? playColors[selectedPlays.indexOf(isSelected) % playColors.length] : null;

                return (
                  <button
                    key={`${play.gameId}_${play.playId}`}
                    onClick={() => togglePlaySelection(play)}
                    className={`px-3 py-1 rounded text-xs font-medium transition-all ${
                      isSelected 
                        ? 'ring-2 ring-offset-1 ring-offset-gray-800' 
                        : 'bg-gray-700 hover:bg-gray-600'
                    }`}
                    style={isSelected ? { 
                      backgroundColor: color.main,
                      ringColor: color.light
                    } : {}}
                  >
                    {play.wrName.split(' ').pop()} • {play.passResult === 'C' ? '✓' : '✗'}
                  </button>
                );
              })}
            </div>
          </div>
        </div>

        <div className="bg-gray-800 rounded-lg p-4 mb-4">
          <canvas
            ref={canvasRef}
            width={1000}
            height={550}
            className="w-full rounded-lg shadow-xl"
          />

          <div className="mt-4 space-y-3">
            <div className="flex items-center gap-2">
              <button
                onClick={handleReset}
                className="p-2 bg-gray-700 hover:bg-gray-600 rounded-lg"
                title="Reset"
                disabled={isRecording}
              >
                <RotateCcw className="w-4 h-4" />
              </button>
              <button
                onClick={handlePlayPause}
                className="p-2 bg-blue-600 hover:bg-blue-500 rounded-lg"
                title={isPlaying ? 'Pause' : 'Play'}
                disabled={isRecording || selectedPlays.length === 0}
              >
                {isPlaying ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
              </button>

              {!isRecording ? (
                <button
                  onClick={startRecording}
                  className="p-2 bg-red-600 hover:bg-red-500 rounded-lg flex items-center gap-2"
                  title="Record Video"
                  disabled={selectedPlays.length === 0}
                >
                  <Download className="w-4 h-4" />
                  <span className="text-xs">Record</span>
                </button>
              ) : (
                <button
                  onClick={stopRecording}
                  className="p-2 bg-red-600 animate-pulse rounded-lg flex items-center gap-2"
                  title="Stop Recording"
                >
                  <div className="w-3 h-3 bg-white rounded-full" />
                  <span className="text-xs">Recording...</span>
                </button>
              )}

              <input
                type="range"
                min="0"
                max={maxFrames - 1}
                value={currentFrame}
                onChange={(e) => setCurrentFrame(parseInt(e.target.value))}
                className="flex-1"
                disabled={isRecording || selectedPlays.length === 0}
              />
              <div className="text-xs text-gray-400 w-20 text-right">
                Frame {currentFrame + 1}/{maxFrames}
              </div>
            </div>

            <div className="flex items-center gap-3">
              <label className="text-xs text-gray-400">Speed:</label>
              <input
                type="range"
                min="30"
                max="200"
                step="10"
                value={playSpeed}
                onChange={(e) => setPlaySpeed(parseInt(e.target.value))}
                className="flex-1"
                disabled={isRecording}
              />
              <span className="text-xs text-gray-400 w-16">{(1000/playSpeed).toFixed(1)}x</span>
            </div>
          </div>
        </div>

        {selectedPlays.length > 0 && (
          <div className="bg-gray-800 rounded-lg p-4">
            <h3 className="text-sm font-semibold mb-3">Quick Stats Comparison</h3>
            <div className="overflow-x-auto">
              <table className="w-full text-sm">
                <thead>
                  <tr className="text-gray-400 border-b border-gray-700">
                    <th className="text-left py-2">Player</th>
                    <th className="text-right py-2">Max Sep</th>
                    <th className="text-right py-2">Time to Max</th>
                    <th className="text-right py-2">Avg Sep</th>
                    <th className="text-right py-2">Initial Sep</th>
                    <th className="text-center py-2">Result</th>
                  </tr>
                </thead>
                <tbody>
                  {selectedPlays.map((play, index) => {
                    const color = playColors[index % playColors.length];
                    return (
                      <tr key={`${play.gameId}_${play.playId}`} className="border-b border-gray-700">
                        <td className="py-2 flex items-center gap-2">
                          <div className="w-3 h-3 rounded-full" style={{ backgroundColor: color.main }}></div>
                          {play.wrName}
                        </td>
                        <td className="text-right py-2 font-mono">{play.maxSeparation.toFixed(1)} yds</td>
                        <td className="text-right py-2 font-mono">{play.maxSeparationTime.toFixed(1)}s</td>
                        <td className="text-right py-2 font-mono">{play.avgSeparation.toFixed(1)} yds</td>
                        <td className="text-right py-2 font-mono">{play.initialSeparation.toFixed(1)} yds</td>
                        <td className="text-center py-2">
                          <span className={`px-2 py-1 rounded text-xs font-bold ${
                            play.passResult === 'C' ? 'bg-green-600' : 'bg-red-600'
                          }`}>
                            {play.passResult === 'C' ? 'COMPLETE' : 'INCOMPLETE'}
                          </span>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RouteComparisonVisualizer;
