import React, { useEffect, useState, useRef, useMemo } from 'react';

// Sample data - replace with your full dataset
const PLAYER_DATA = [
  { player: "Rashee Rice", wos: 0.832, exp: 3.103, targets: 70, pass_length: 7.76, time_to_throw: 2.80, def_avg_speed: 1.58, sep_at_arrival: 3.93 },
  { player: "Jerry Jeudy", wos: 0.551, exp: 3.152, targets: 66, pass_length: 15.08, time_to_throw: 3.12, def_avg_speed: 1.44, sep_at_arrival: 3.70 },
  { player: "Ja'Marr Chase", wos: 1.12, exp: 2.95, targets: 145, pass_length: 12.5, time_to_throw: 2.95, def_avg_speed: 1.62, sep_at_arrival: 4.1 },
  { player: "CeeDee Lamb", wos: 0.95, exp: 3.01, targets: 158, pass_length: 10.2, time_to_throw: 2.88, def_avg_speed: 1.55, sep_at_arrival: 3.95 },
  { player: "Tyreek Hill", wos: 1.25, exp: 2.85, targets: 142, pass_length: 14.8, time_to_throw: 3.05, def_avg_speed: 1.70, sep_at_arrival: 4.25 },
  { player: "A.J. Brown", wos: 0.88, exp: 3.15, targets: 110, pass_length: 13.2, time_to_throw: 3.10, def_avg_speed: 1.48, sep_at_arrival: 3.85 },
  { player: "Davante Adams", wos: 0.72, exp: 3.08, targets: 98, pass_length: 11.5, time_to_throw: 2.92, def_avg_speed: 1.52, sep_at_arrival: 3.78 },
  { player: "Stefon Diggs", wos: 0.65, exp: 3.22, targets: 125, pass_length: 9.8, time_to_throw: 2.75, def_avg_speed: 1.45, sep_at_arrival: 3.65 },
  { player: "Amon-Ra St. Brown", wos: 0.78, exp: 2.98, targets: 152, pass_length: 8.5, time_to_throw: 2.68, def_avg_speed: 1.58, sep_at_arrival: 3.72 },
  { player: "Chris Olave", wos: 0.55, exp: 3.35, targets: 88, pass_length: 15.5, time_to_throw: 3.22, def_avg_speed: 1.38, sep_at_arrival: 3.55 },
  { player: "Garrett Wilson", wos: 0.68, exp: 3.18, targets: 132, pass_length: 11.2, time_to_throw: 2.95, def_avg_speed: 1.50, sep_at_arrival: 3.68 },
  { player: "DeVonta Smith", wos: 0.82, exp: 3.05, targets: 118, pass_length: 12.8, time_to_throw: 3.02, def_avg_speed: 1.55, sep_at_arrival: 3.88 },
];

const RouteComparisonVisualizer = () => {
  const [phase, setPhase] = useState(0);
  const [activePlayer, setActivePlayer] = useState(null);
  const [showStats, setShowStats] = useState(false);
  const [animatedPlayers, setAnimatedPlayers] = useState([]);
  const [showComparison, setShowComparison] = useState(false);
  const [highlightFeature, setHighlightFeature] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const timeoutRef = useRef([]);

  // Chart dimensions
  const W = 1200;
  const H = 700;
  const P = 100;

  // Calculate scales
  const { xScale, yScale, xMin, xMax, yMin, yMax } = useMemo(() => {
    const xs = PLAYER_DATA.map(d => d.wos);
    const ys = PLAYER_DATA.map(d => d.exp);
    const xMin = Math.min(...xs) - 0.15;
    const xMax = Math.max(...xs) + 0.15;
    const yMin = Math.min(...ys) - 0.15;
    const yMax = Math.max(...ys) + 0.15;

    return {
      xScale: v => P + ((v - xMin) / (xMax - xMin)) * (W - P * 2),
      yScale: v => H - P - ((v - yMin) / (yMax - yMin)) * (H - P * 2),
      xMin, xMax, yMin, yMax
    };
  }, []);

  // Animation sequence
  const runAnimation = () => {
    // Clear existing timeouts
    timeoutRef.current.forEach(t => clearTimeout(t));
    timeoutRef.current = [];

    setIsPlaying(true);
    setPhase(0);
    setAnimatedPlayers([]);
    setActivePlayer(null);
    setShowStats(false);
    setShowComparison(false);
    setHighlightFeature(null);

    // Phase 1: Title reveal
    timeoutRef.current.push(setTimeout(() => setPhase(1), 500));

    // Phase 2: Axes draw in
    timeoutRef.current.push(setTimeout(() => setPhase(2), 1500));

    // Phase 3: Players enter one by one
    PLAYER_DATA.forEach((player, idx) => {
      timeoutRef.current.push(setTimeout(() => {
        setAnimatedPlayers(prev => [...prev, player]);
      }, 2500 + idx * 300));
    });

    // Phase 4: Highlight creators (WOS > EXP delta)
    timeoutRef.current.push(setTimeout(() => {
      setPhase(4);
      setHighlightFeature('creators');
    }, 2500 + PLAYER_DATA.length * 300 + 1000));

    // Phase 5: Show comparison mode
    timeoutRef.current.push(setTimeout(() => {
      setShowComparison(true);
      setHighlightFeature(null);
    }, 2500 + PLAYER_DATA.length * 300 + 3500));

    // Phase 6: Cycle through features
    const features = ['pass_length', 'time_to_throw', 'def_avg_speed', 'targets'];
    features.forEach((feature, idx) => {
      timeoutRef.current.push(setTimeout(() => {
        setHighlightFeature(feature);
      }, 2500 + PLAYER_DATA.length * 300 + 5000 + idx * 2500));
    });

    // Phase 7: Final state
    timeoutRef.current.push(setTimeout(() => {
      setHighlightFeature(null);
      setPhase(7);
      setIsPlaying(false);
    }, 2500 + PLAYER_DATA.length * 300 + 5000 + features.length * 2500 + 1000));
  };

  // Get player radius based on feature highlight
  const getPlayerRadius = (player) => {
    if (!highlightFeature) return 22;

    switch(highlightFeature) {
      case 'pass_length':
        return 12 + (player.pass_length / 20) * 25;
      case 'time_to_throw':
        return 12 + (player.time_to_throw / 4) * 25;
      case 'def_avg_speed':
        return 12 + (player.def_avg_speed / 2) * 25;
      case 'targets':
        return 12 + (player.targets / 160) * 30;
      case 'creators':
        return player.wos > (player.exp - 2.5) ? 30 : 18;
      default:
        return 22;
    }
  };

  // Get player color based on feature highlight
  const getPlayerColor = (player) => {
    const delta = player.wos - (player.exp - 2.5);

    if (highlightFeature === 'creators') {
      return delta > 0.3 ? '#00ff88' : delta > 0 ? '#88ffaa' : '#ff6b6b';
    }

    if (highlightFeature === 'pass_length') {
      const intensity = player.pass_length / 16;
      return `hsl(${200 + intensity * 60}, 80%, ${50 + intensity * 20}%)`;
    }

    if (highlightFeature === 'time_to_throw') {
      const intensity = player.time_to_throw / 3.5;
      return `hsl(${30 + intensity * 30}, 90%, ${50 + intensity * 15}%)`;
    }

    if (highlightFeature === 'def_avg_speed') {
      const intensity = player.def_avg_speed / 1.8;
      return `hsl(${280 + intensity * 40}, 75%, ${45 + intensity * 20}%)`;
    }

    if (highlightFeature === 'targets') {
      const intensity = player.targets / 160;
      return `hsl(${150 - intensity * 80}, 70%, ${40 + intensity * 25}%)`;
    }

    return delta > 0 ? '#00ff88' : '#ff6b6b';
  };

  const featureLabels = {
    pass_length: 'Pass Length (yards)',
    time_to_throw: 'Time to Throw (seconds)',
    def_avg_speed: 'Defender Avg Speed (mph)',
    targets: 'Total Targets',
    creators: 'Separation Creators'
  };

  return (
    <div className="min-h-screen bg-[#0a0a12] flex flex-col items-center justify-center p-8 overflow-hidden"
         style={{ fontFamily: "'Space Grotesk', 'Helvetica Neue', sans-serif" }}>

      {/* Animated Background */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute inset-0 bg-gradient-to-br from-[#0a0a12] via-[#12121f] to-[#0a0a12]" />
        <div className="absolute top-0 left-0 w-full h-full opacity-30"
             style={{
               backgroundImage: `radial-gradient(circle at 20% 80%, #00ff8820 0%, transparent 50%),
                                radial-gradient(circle at 80% 20%, #ff6b6b20 0%, transparent 50%),
                                radial-gradient(circle at 50% 50%, #4488ff15 0%, transparent 60%)`
             }} />
        {/* Grid pattern */}
        <svg className="absolute inset-0 w-full h-full opacity-10">
          <defs>
            <pattern id="grid" width="60" height="60" patternUnits="userSpaceOnUse">
              <path d="M 60 0 L 0 0 0 60" fill="none" stroke="#ffffff" strokeWidth="0.5"/>
            </pattern>
          </defs>
          <rect width="100%" height="100%" fill="url(#grid)" />
        </svg>
      </div>

      {/* Title */}
      <div className={`mb-8 text-center z-10 transition-all duration-1000 transform
                       ${phase >= 1 ? 'opacity-100 translate-y-0' : 'opacity-0 -translate-y-10'}`}>
        <h1 className="text-6xl font-black text-white tracking-tight mb-2"
            style={{ textShadow: '0 0 60px rgba(0, 255, 136, 0.3)' }}>
          WR SEPARATION
          <span className="text-transparent bg-clip-text bg-gradient-to-r from-[#00ff88] to-[#4488ff]"> LANDSCAPE</span>
        </h1>
        <p className="text-gray-400 text-xl tracking-widest uppercase">
          Who Creates vs. Who Gets Lucky
        </p>
      </div>

      {/* Feature Highlight Indicator */}
      <div className={`mb-6 z-10 transition-all duration-500 h-12
                       ${highlightFeature ? 'opacity-100' : 'opacity-0'}`}>
        <div className="px-8 py-3 bg-gradient-to-r from-[#00ff8830] to-[#4488ff30] rounded-full
                        border border-[#00ff8850] backdrop-blur-sm">
          <span className="text-[#00ff88] font-bold text-lg tracking-wider uppercase">
            {highlightFeature ? featureLabels[highlightFeature] : ''}
          </span>
        </div>
      </div>

      {/* Main Chart */}
      <div className="relative z-10">
        <svg width={W} height={H} className="overflow-visible">
          {/* Glow filters */}
          <defs>
            <filter id="glow" x="-50%" y="-50%" width="200%" height="200%">
              <feGaussianBlur stdDeviation="4" result="coloredBlur"/>
              <feMerge>
                <feMergeNode in="coloredBlur"/>
                <feMergeNode in="SourceGraphic"/>
              </feMerge>
            </filter>
            <filter id="strongGlow" x="-100%" y="-100%" width="300%" height="300%">
              <feGaussianBlur stdDeviation="8" result="coloredBlur"/>
              <feMerge>
                <feMergeNode in="coloredBlur"/>
                <feMergeNode in="SourceGraphic"/>
              </feMerge>
            </filter>
            <linearGradient id="axisGradient" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="#00ff88" stopOpacity="0.2"/>
              <stop offset="50%" stopColor="#4488ff" stopOpacity="0.5"/>
              <stop offset="100%" stopColor="#ff6b6b" stopOpacity="0.2"/>
            </linearGradient>
          </defs>

          {/* Background area */}
          <rect x={P} y={P} width={W - P * 2} height={H - P * 2} 
                fill="#ffffff05" rx="8"
                className={`transition-all duration-1000 ${phase >= 2 ? 'opacity-100' : 'opacity-0'}`}/>

          {/* Quadrant backgrounds */}
          <g className={`transition-all duration-1000 ${phase >= 4 ? 'opacity-100' : 'opacity-0'}`}>
            {/* Top Right - Achievers */}
            <rect x={xScale((xMin + xMax) / 2)} y={P} 
                  width={(W - P * 2) / 2} height={(H - P * 2) / 2}
                  fill="#00ff8808"/>
            {/* Bottom Left - Underperformers */}
            <rect x={P} y={yScale((yMin + yMax) / 2)} 
                  width={(W - P * 2) / 2} height={(H - P * 2) / 2}
                  fill="#ff6b6b08"/>
          </g>

          {/* Axes */}
          <g className={`transition-all duration-1000 ${phase >= 2 ? 'opacity-100' : 'opacity-0'}`}>
            {/* X Axis */}
            <line x1={P} y1={H - P} x2={W - P} y2={H - P} 
                  stroke="url(#axisGradient)" strokeWidth="2"/>
            {/* Y Axis */}
            <line x1={P} y1={P} x2={P} y2={H - P} 
                  stroke="url(#axisGradient)" strokeWidth="2"/>

            {/* Center lines */}
            <line x1={xScale((xMin + xMax) / 2)} y1={P} 
                  x2={xScale((xMin + xMax) / 2)} y2={H - P}
                  stroke="#ffffff20" strokeWidth="1" strokeDasharray="8,8"/>
            <line x1={P} y1={yScale((yMin + yMax) / 2)} 
                  x2={W - P} y2={yScale((yMin + yMax) / 2)}
                  stroke="#ffffff20" strokeWidth="1" strokeDasharray="8,8"/>

            {/* Axis Labels */}
            <text x={W / 2} y={H - 30} textAnchor="middle" 
                  className="fill-white text-lg font-bold tracking-widest uppercase">
              Created Separation (WOS)
            </text>
            <text x={35} y={H / 2} textAnchor="middle" 
                  className="fill-white text-lg font-bold tracking-widest uppercase"
                  transform={`rotate(-90, 35, ${H / 2})`}>
              Expected Separation
            </text>

            {/* Axis values */}
            <text x={P} y={H - P + 30} textAnchor="middle" className="fill-gray-400 text-sm">
              {xMin.toFixed(1)}
            </text>
            <text x={W - P} y={H - P + 30} textAnchor="middle" className="fill-gray-400 text-sm">
              {xMax.toFixed(1)}
            </text>
            <text x={P - 20} y={H - P} textAnchor="end" className="fill-gray-400 text-sm">
              {yMin.toFixed(1)}
            </text>
            <text x={P - 20} y={P + 5} textAnchor="end" className="fill-gray-400 text-sm">
              {yMax.toFixed(1)}
            </text>
          </g>

          {/* Quadrant Labels */}
          <g className={`transition-all duration-1000 ${phase >= 4 ? 'opacity-100' : 'opacity-0'}`}>
            <text x={W - P - 20} y={P + 40} textAnchor="end" 
                  className="fill-[#00ff88] text-xl font-black uppercase" filter="url(#glow)">
              The Creators
            </text>
            <text x={P + 20} y={H - P - 20} textAnchor="start" 
                  className="fill-[#ff6b6b] text-xl font-black uppercase" filter="url(#glow)">
              Underperformers
            </text>
          </g>

          {/* Player nodes */}
          {animatedPlayers.map((player, idx) => {
            const x = xScale(player.wos);
            const y = yScale(player.exp);
            const radius = getPlayerRadius(player);
            const color = getPlayerColor(player);
            const isHovered = activePlayer?.player === player.player;

            return (
              <g key={player.player}
                 className="cursor-pointer"
                 style={{
                   animation: `nodeEnter 0.6s ease-out forwards`,
                   animationDelay: `${idx * 0.05}s`,
                   opacity: 0,
                 }}
                 onMouseEnter={() => setActivePlayer(player)}
                 onMouseLeave={() => setActivePlayer(null)}>

                {/* Outer glow ring */}
                <circle cx={x} cy={y} r={radius + 8}
                        fill="none" stroke={color} strokeWidth="2"
                        opacity={isHovered ? 0.6 : 0.2}
                        filter="url(#strongGlow)"
                        style={{ transition: 'all 0.4s ease-out' }}/>

                {/* Main circle */}
                <circle cx={x} cy={y} r={radius}
                        fill={color}
                        opacity={0.9}
                        filter="url(#glow)"
                        style={{ 
                          transition: 'all 0.4s ease-out',
                          transform: isHovered ? 'scale(1.2)' : 'scale(1)',
                          transformOrigin: `${x}px ${y}px`
                        }}/>

                {/* Inner highlight */}
                <circle cx={x - radius * 0.3} cy={y - radius * 0.3} r={radius * 0.3}
                        fill="white" opacity="0.3"/>

                {/* Player name */}
                <text x={x} y={y + radius + 18} textAnchor="middle"
                      className="fill-white text-xs font-bold tracking-wider uppercase"
                      style={{ 
                        opacity: isHovered || radius > 25 ? 1 : 0.7,
                        transition: 'opacity 0.3s'
                      }}>
                  {player.player.split(' ').pop()}
                </text>
              </g>
            );
          })}

          {/* Comparison trails */}
          {showComparison && animatedPlayers.map((player, idx) => {
            const x = xScale(player.wos);
            const y = yScale(player.exp);
            const delta = player.wos - (player.exp - 2.5);

            return (
              <g key={`trail-${player.player}`}
                 style={{ animation: 'fadeIn 0.5s ease-out forwards' }}>
                <line 
                  x1={x} y1={y}
                  x2={x} y2={y + (delta > 0 ? -50 : 50)}
                  stroke={delta > 0 ? '#00ff88' : '#ff6b6b'}
                  strokeWidth="2"
                  strokeDasharray="4,4"
                  opacity="0.4"
                />
              </g>
            );
          })}
        </svg>

        {/* Hover tooltip */}
        {activePlayer && (
          <div className="absolute pointer-events-none bg-[#1a1a2e]/95 backdrop-blur-xl 
                          border border-[#00ff8850] rounded-xl p-5 min-w-[280px] shadow-2xl
                          transform -translate-x-1/2"
               style={{
                 left: xScale(activePlayer.wos),
                 top: yScale(activePlayer.exp) - 180,
                 animation: 'tooltipEnter 0.3s ease-out forwards'
               }}>
            <div className="text-2xl font-black text-white mb-3 pb-2 border-b border-[#ffffff20]">
              {activePlayer.player}
            </div>
            <div className="grid grid-cols-2 gap-3 text-sm">
              <div>
                <span className="text-gray-400 block text-xs uppercase tracking-wider">Created Sep</span>
                <span className="text-[#00ff88] font-bold text-lg">{activePlayer.wos.toFixed(3)}</span>
              </div>
              <div>
                <span className="text-gray-400 block text-xs uppercase tracking-wider">Expected Sep</span>
                <span className="text-[#4488ff] font-bold text-lg">{activePlayer.exp.toFixed(3)}</span>
              </div>
              <div>
                <span className="text-gray-400 block text-xs uppercase tracking-wider">Targets</span>
                <span className="text-white font-bold">{activePlayer.targets}</span>
              </div>
              <div>
                <span className="text-gray-400 block text-xs uppercase tracking-wider">Pass Length</span>
                <span className="text-white font-bold">{activePlayer.pass_length.toFixed(1)} yd</span>
              </div>
              <div>
                <span className="text-gray-400 block text-xs uppercase tracking-wider">Time to Throw</span>
                <span className="text-white font-bold">{activePlayer.time_to_throw.toFixed(2)}s</span>
              </div>
              <div>
                <span className="text-gray-400 block text-xs uppercase tracking-wider">Def Speed</span>
                <span className="text-white font-bold">{activePlayer.def_avg_speed.toFixed(2)} mph</span>
              </div>
            </div>
            <div className="mt-3 pt-3 border-t border-[#ffffff20]">
              <span className="text-gray-400 text-xs uppercase tracking-wider">Separation Delta</span>
              <div className={`text-xl font-black ${activePlayer.wos > (activePlayer.exp - 2.5) ? 'text-[#00ff88]' : 'text-[#ff6b6b]'}`}>
                {activePlayer.wos > (activePlayer.exp - 2.5) ? '+' : ''}{(activePlayer.wos - (activePlayer.exp - 2.5)).toFixed(3)}
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Control Panel */}
      <div className="mt-8 z-10 flex gap-4">
        <button
          onClick={runAnimation}
          disabled={isPlaying}
          className={`px-8 py-4 rounded-full font-bold text-lg tracking-wider uppercase
                      transition-all duration-300 transform hover:scale-105
                      ${isPlaying 
                        ? 'bg-gray-700 text-gray-400 cursor-not-allowed' 
                        : 'bg-gradient-to-r from-[#00ff88] to-[#4488ff] text-black hover:shadow-lg hover:shadow-[#00ff8850]'}`}
        >
          {isPlaying ? '⏳ Playing...' : '▶ Play Animation'}
        </button>

        {/* Manual feature controls */}
        {!isPlaying && phase >= 4 && (
          <div className="flex gap-2">
            {['creators', 'targets', 'pass_length', 'time_to_throw', 'def_avg_speed'].map(feature => (
              <button
                key={feature}
                onClick={() => setHighlightFeature(highlightFeature === feature ? null : feature)}
                className={`px-4 py-2 rounded-lg text-sm font-bold uppercase tracking-wider
                            transition-all duration-300
                            ${highlightFeature === feature 
                              ? 'bg-[#00ff88] text-black' 
                              : 'bg-[#ffffff10] text-white hover:bg-[#ffffff20]'}`}
              >
                {feature.replace('_', ' ')}
              </button>
            ))}
          </div>
        )}
      </div>

      {/* Legend */}
      <div className={`mt-6 z-10 flex gap-8 text-sm transition-all duration-500 ${phase >= 4 ? 'opacity-100' : 'opacity-0'}`}>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded-full bg-[#00ff88]" />
          <span className="text-gray-400">Creates Above Expected</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded-full bg-[#ff6b6b]" />
          <span className="text-gray-400">Below Expected</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-3 h-3 rounded-full bg-white opacity-50" />
          <span className="text-gray-400">→</span>
          <div className="w-6 h-6 rounded-full bg-white opacity-50" />
          <span className="text-gray-400">Size = Feature Value</span>
        </div>
      </div>

      {/* CSS Keyframes */}
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Space+Grotesk:wght@400;700&display=swap');

        @keyframes nodeEnter {
          0% {
            opacity: 0;
            transform: scale(0) translateY(50px);
          }
          60% {
            transform: scale(1.2) translateY(-5px);
          }
          100% {
            opacity: 1;
            transform: scale(1) translateY(0);
          }
        }

        @keyframes tooltipEnter {
          0% {
            opacity: 0;
            transform: translateX(-50%) translateY(10px);
          }
          100% {
            opacity: 1;
            transform: translateX(-50%) translateY(0);
          }
        }

        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }

        @keyframes pulse {
          0%, 100% { opacity: 0.3; transform: scale(1); }
          50% { opacity: 0.6; transform: scale(1.05); }
        }
      `}</style>
    </div>
  );
};

export default RouteComparisonVisualizer;
