import React, { useState, useEffect, useRef } from 'react';
import { Play, Pause, SkipBack, SkipForward, Download, ChevronLeft, ChevronRight } from 'lucide-react';

const NFLPlayVisualizer = () => {
  const [allPlays, setAllPlays] = useState([]);
  const [games, setGames] = useState([]);
  const [selectedGame, setSelectedGame] = useState(null);
  const [filteredPlays, setFilteredPlays] = useState([]);
  const [currentPlayIndex, setCurrentPlayIndex] = useState(0);
  const [frame, setFrame] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [playSpeed, setPlaySpeed] = useState(100);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const canvasRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const recordedChunksRef = useRef([]);

  const parseCSV = (text) => {
    const lines = text.split('\n');
    const headers = lines[0].split(',').map(h => h.trim());

    const data = lines.slice(1)
      .filter(line => line.trim())
      .map(line => {
        const values = line.split(',');
        const obj = {};
        headers.forEach((header, i) => {
          obj[header] = values[i]?.trim();
        });
        return obj;
      });

    return data;
  };

  const parseCSVData = (inputData, outputData, suppData) => {
    const playMap = new Map();

    inputData.forEach(row => {
      const key = `${row.game_id}_${row.play_id}`;
      if (!playMap.has(key)) {
        playMap.set(key, {
          gameId: row.game_id,
          playId: row.play_id,
          inputFrames: [],
          outputFrames: [],
          playInfo: null
        });
      }
      playMap.get(key).inputFrames.push(row);
    });

    outputData.forEach(row => {
      const key = `${row.game_id}_${row.play_id}`;
      if (playMap.has(key)) {
        playMap.get(key).outputFrames.push(row);
      }
    });

    suppData.forEach(row => {
      const key = `${row.game_id}_${row.play_id}`;
      if (playMap.has(key)) {
        playMap.get(key).playInfo = row;
      }
    });

    const plays = [];
    const gameSet = new Set();

    playMap.forEach((playData, key) => {
      gameSet.add(playData.gameId);

      const allFrames = [
        ...playData.inputFrames.map(f => ({ ...f, source: 'input' })),
        ...playData.outputFrames.map(f => ({ ...f, source: 'output' }))
      ].sort((a, b) => parseFloat(a.frame_id) - parseFloat(b.frame_id));

      const frameMap = new Map();
      allFrames.forEach(row => {
        const frameId = parseFloat(row.frame_id);
        if (!frameMap.has(frameId)) {
          frameMap.set(frameId, {
            frameId: frameId,
            players: [],
            ball: null,
            separation: 0
          });
        }

        const frame = frameMap.get(frameId);

        if (row.display_name === 'football' || row.nfl_id === 'ball' || !row.player_role) {
          frame.ball = {
            x: parseFloat(row.x),
            y: parseFloat(row.y)
          };
        } else {
          frame.players.push({
            x: parseFloat(row.x),
            y: parseFloat(row.y),
            role: row.player_role || 'other',
            position: row.player_position || '',
            side: row.player_side || '',
            name: row.player_name || '',
            nflId: row.nfl_id
          });
        }
      });

      frameMap.forEach((frame) => {
        const wr = frame.players.find(p => p.role === 'Targeted Receiver');
        const defenders = frame.players.filter(p => p.role === 'Defensive Coverage');

        if (wr && defenders.length > 0) {
          const distances = defenders.map(def => 
            Math.sqrt(Math.pow(wr.x - def.x, 2) + Math.pow(wr.y - def.y, 2))
          );
          frame.separation = Math.min(...distances);
        }
      });

      const frames = Array.from(frameMap.values());

      if (frames.length > 0) {
        const wr = frames[0].players.find(p => p.role === 'Targeted Receiver');

        plays.push({
          gameId: playData.gameId,
          playId: playData.playId,
          description: playData.playInfo?.route_of_targeted_receiver || `Play ${playData.playId}`,
          passResult: playData.playInfo?.pass_result || 'N/A',
          wrName: wr?.name || 'Unknown',
          frames: frames
        });
      }
    });

    const gameList = Array.from(gameSet).map(gameId => ({
      gameId: gameId
    }));

    return { plays, games: gameList };
  };

  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);

        const [inputRes, outputRes, suppRes] = await Promise.all([
          fetch('/data/input_all.csv'),
          fetch('/data/output_all.csv'),
          fetch('/data/supplementary_data.csv')
        ]);

        if (!inputRes.ok || !outputRes.ok || !suppRes.ok) {
          throw new Error('Failed to load data files');
        }

        const [inputText, outputText, suppText] = await Promise.all([
          inputRes.text(),
          outputRes.text(),
          suppRes.text()
        ]);

        const inputData = parseCSV(inputText);
        const outputData = parseCSV(outputText);
        const suppData = parseCSV(suppText);

        const { plays, games } = parseCSVData(inputData, outputData, suppData);

        setAllPlays(plays);
        setGames(games);

        if (games.length > 0) {
          setSelectedGame(games[0].gameId);
          setFilteredPlays(plays.filter(p => p.gameId === games[0].gameId));
        }

        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    loadData();
  }, []);

  useEffect(() => {
    if (selectedGame && allPlays.length > 0) {
      const plays = allPlays.filter(p => p.gameId === selectedGame);
      setFilteredPlays(plays);
      setCurrentPlayIndex(0);
      setFrame(0);
      setIsPlaying(false);
    }
  }, [selectedGame, allPlays]);

  useEffect(() => {
    if (!isPlaying || filteredPlays.length === 0) return;

    const currentPlay = filteredPlays[currentPlayIndex];
    if (!currentPlay) return;

    const interval = setInterval(() => {
      setFrame(prev => {
        if (prev >= currentPlay.frames.length - 1) {
          setIsPlaying(false);
          return prev;
        }
        return prev + 1;
      });
    }, playSpeed);

    return () => clearInterval(interval);
  }, [isPlaying, currentPlayIndex, filteredPlays, playSpeed]);

  useEffect(() => {
    if (filteredPlays.length === 0 || !canvasRef.current) return;

    const currentPlay = filteredPlays[currentPlayIndex];
    if (!currentPlay || !currentPlay.frames[frame]) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const width = canvas.width;
    const height = canvas.height;

    // Dark elegant background
    ctx.fillStyle = '#0a0a0a';
    ctx.fillRect(0, 0, width, height);

    // Subtle field grid - very minimal
    ctx.strokeStyle = 'rgba(255, 255, 255, 0.03)';
    ctx.lineWidth = 1;

    // Horizontal yard lines
    for (let i = 0; i <= 12; i++) {
      const y = (i / 12) * height;
      ctx.beginPath();
      ctx.moveTo(0, y);
      ctx.lineTo(width, y);
      ctx.stroke();
    }

    // Hash marks area - subtle
    ctx.fillStyle = 'rgba(255, 255, 255, 0.02)';
    ctx.fillRect(width * 0.38, 0, width * 0.24, height);

    const currentFrame = currentPlay.frames[frame];
    const initialSep = currentPlay.frames[0].separation;
    const maxSep = Math.max(...currentPlay.frames.map(f => f.separation));
    const sepGain = currentFrame.separation - initialSep;
    const progress = frame / (currentPlay.frames.length - 1);

    const wrTarget = currentFrame.players.find(p => p.role === 'Targeted Receiver');
    const defenders = currentFrame.players.filter(p => p.role === 'Defensive Coverage');
    const qb = currentFrame.players.find(p => p.role === 'Passer');

    // Draw trail for WR (last 15 frames)
    if (wrTarget) {
      const trailLength = 15;
      const startFrame = Math.max(0, frame - trailLength);

      for (let i = startFrame; i < frame; i++) {
        const trailFrame = currentPlay.frames[i];
        const trailWr = trailFrame?.players.find(p => p.role === 'Targeted Receiver');
        if (trailWr) {
          const alpha = ((i - startFrame) / trailLength) * 0.4;
          const x = (trailWr.x / 120) * width;
          const y = (trailWr.y / 53.3) * height;

          ctx.fillStyle = `rgba(59, 130, 246, ${alpha})`;
          ctx.beginPath();
          ctx.arc(x, y, 3, 0, Math.PI * 2);
          ctx.fill();
        }
      }
    }

    // Draw separation line first (behind players)
    if (wrTarget && defenders.length > 0) {
      const closestDef = defenders.reduce((closest, def) => {
        const dist = Math.sqrt(Math.pow(wrTarget.x - def.x, 2) + Math.pow(wrTarget.y - def.y, 2));
        return dist < closest.dist ? { player: def, dist } : closest;
      }, { player: defenders[0], dist: Infinity });

      const x1 = (wrTarget.x / 120) * width;
      const y1 = (wrTarget.y / 53.3) * height;
      const x2 = (closestDef.player.x / 120) * width;
      const y2 = (closestDef.player.y / 53.3) * height;

      // Gradient line for separation
      const gradient = ctx.createLinearGradient(x1, y1, x2, y2);
      gradient.addColorStop(0, 'rgba(59, 130, 246, 0.6)');
      gradient.addColorStop(1, 'rgba(239, 68, 68, 0.6)');

      ctx.strokeStyle = gradient;
      ctx.lineWidth = 2;
      ctx.setLineDash([]);
      ctx.beginPath();
      ctx.moveTo(x1, y1);
      ctx.lineTo(x2, y2);
      ctx.stroke();

      // Separation distance label at midpoint
      const midX = (x1 + x2) / 2;
      const midY = (y1 + y2) / 2;

      ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
      ctx.beginPath();
      ctx.roundRect(midX - 22, midY - 10, 44, 20, 4);
      ctx.fill();

      ctx.fillStyle = '#ffffff';
      ctx.font = '600 11px system-ui';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      ctx.fillText(`${currentFrame.separation.toFixed(1)}`, midX, midY);
    }

    // Draw players
    currentFrame.players.forEach(player => {
      const x = (player.x / 120) * width;
      const y = (player.y / 53.3) * height;

      let fillColor, glowColor, size;

      if (player.role === 'Targeted Receiver') {
        fillColor = '#3b82f6';
        glowColor = 'rgba(59, 130, 246, 0.4)';
        size = 12;

        // Outer glow
        ctx.fillStyle = glowColor;
        ctx.beginPath();
        ctx.arc(x, y, size + 6, 0, Math.PI * 2);
        ctx.fill();

      } else if (player.role === 'Defensive Coverage') {
        fillColor = '#ef4444';
        glowColor = 'rgba(239, 68, 68, 0.3)';
        size = 10;

        ctx.fillStyle = glowColor;
        ctx.beginPath();
        ctx.arc(x, y, size + 4, 0, Math.PI * 2);
        ctx.fill();

      } else if (player.role === 'Passer') {
        fillColor = '#10b981';
        glowColor = 'rgba(16, 185, 129, 0.3)';
        size = 10;

        ctx.fillStyle = glowColor;
        ctx.beginPath();
        ctx.arc(x, y, size + 4, 0, Math.PI * 2);
        ctx.fill();

      } else if (player.side === wrTarget?.side) {
        fillColor = 'rgba(59, 130, 246, 0.5)';
        size = 6;
      } else {
        fillColor = 'rgba(239, 68, 68, 0.5)';
        size = 6;
      }

      // Main player dot
      ctx.fillStyle = fillColor;
      ctx.beginPath();
      ctx.arc(x, y, size, 0, Math.PI * 2);
      ctx.fill();

      // Inner highlight for key players
      if (player.role === 'Targeted Receiver' || player.role === 'Defensive Coverage' || player.role === 'Passer') {
        ctx.fillStyle = 'rgba(255, 255, 255, 0.3)';
        ctx.beginPath();
        ctx.arc(x - size * 0.2, y - size * 0.2, size * 0.4, 0, Math.PI * 2);
        ctx.fill();
      }
    });

    // Top info bar - Route and WR Name
    const topBarHeight = 56;
    ctx.fillStyle = 'rgba(0, 0, 0, 0.85)';
    ctx.fillRect(0, 0, width, topBarHeight);

    // Accent line
    ctx.fillStyle = '#3b82f6';
    ctx.fillRect(0, topBarHeight - 2, width, 2);

    // Route name (large, left)
    ctx.fillStyle = '#ffffff';
    ctx.font = '700 24px system-ui';
    ctx.textAlign = 'left';
    ctx.textBaseline = 'middle';
    ctx.fillText(currentPlay.description.toUpperCase(), 20, topBarHeight / 2);

    // WR Name (right side)
    ctx.font = '500 14px system-ui';
    ctx.fillStyle = 'rgba(255, 255, 255, 0.6)';
    ctx.textAlign = 'right';
    ctx.fillText('TARGET', width - 20, topBarHeight / 2 - 10);
    ctx.font = '600 16px system-ui';
    ctx.fillStyle = '#3b82f6';
    ctx.fillText(currentPlay.wrName, width - 20, topBarHeight / 2 + 10);

    // Bottom stats bar
    const bottomBarY = height - 70;
    ctx.fillStyle = 'rgba(0, 0, 0, 0.85)';
    ctx.fillRect(0, bottomBarY, width, 70);

    // Top accent line
    ctx.fillStyle = 'rgba(255, 255, 255, 0.1)';
    ctx.fillRect(0, bottomBarY, width, 1);

    // Stats layout
    const statsY = bottomBarY + 35;
    const statWidth = width / 4;

    // Stat 1: Current Separation
    ctx.textAlign = 'center';
    ctx.font = '500 10px system-ui';
    ctx.fillStyle = 'rgba(255, 255, 255, 0.5)';
    ctx.fillText('SEPARATION', statWidth * 0.5, statsY - 15);
    ctx.font = '700 24px system-ui';
    ctx.fillStyle = '#3b82f6';
    ctx.fillText(`${currentFrame.separation.toFixed(1)}`, statWidth * 0.5, statsY + 10);
    ctx.font = '500 10px system-ui';
    ctx.fillStyle = 'rgba(255, 255, 255, 0.4)';
    ctx.fillText('YDS', statWidth * 0.5, statsY + 28);

    // Stat 2: Separation Gain
    ctx.font = '500 10px system-ui';
    ctx.fillStyle = 'rgba(255, 255, 255, 0.5)';
    ctx.fillText('GAIN', statWidth * 1.5, statsY - 15);
    ctx.font = '700 24px system-ui';
    ctx.fillStyle = sepGain >= 0 ? '#10b981' : '#ef4444';
    ctx.fillText(`${sepGain >= 0 ? '+' : ''}${sepGain.toFixed(1)}`, statWidth * 1.5, statsY + 10);
    ctx.font = '500 10px system-ui';
    ctx.fillStyle = 'rgba(255, 255, 255, 0.4)';
    ctx.fillText('YDS', statWidth * 1.5, statsY + 28);

    // Stat 3: Max Separation
    ctx.font = '500 10px system-ui';
    ctx.fillStyle = 'rgba(255, 255, 255, 0.5)';
    ctx.fillText('PEAK', statWidth * 2.5, statsY - 15);
    ctx.font = '700 24px system-ui';
    ctx.fillStyle = '#ffffff';
    ctx.fillText(`${maxSep.toFixed(1)}`, statWidth * 2.5, statsY + 10);
    ctx.font = '500 10px system-ui';
    ctx.fillStyle = 'rgba(255, 255, 255, 0.4)';
    ctx.fillText('YDS', statWidth * 2.5, statsY + 28);

    // Stat 4: Result
    ctx.font = '500 10px system-ui';
    ctx.fillStyle = 'rgba(255, 255, 255, 0.5)';
    ctx.fillText('RESULT', statWidth * 3.5, statsY - 15);
    ctx.font = '700 18px system-ui';
    const resultColor = currentPlay.passResult === 'C' ? '#10b981' : 
                       currentPlay.passResult === 'I' ? '#ef4444' : '#f59e0b';
    ctx.fillStyle = resultColor;
    const resultText = currentPlay.passResult === 'C' ? 'COMPLETE' : 
                      currentPlay.passResult === 'I' ? 'INCOMPLETE' : currentPlay.passResult;
    ctx.fillText(resultText, statWidth * 3.5, statsY + 10);

    // Timeline/progress indicator
    const timelineY = bottomBarY - 8;
    const timelineWidth = width - 40;
    const timelineX = 20;

    // Background track
    ctx.fillStyle = 'rgba(255, 255, 255, 0.1)';
    ctx.beginPath();
    ctx.roundRect(timelineX, timelineY, timelineWidth, 4, 2);
    ctx.fill();

    // Progress
    ctx.fillStyle = '#3b82f6';
    ctx.beginPath();
    ctx.roundRect(timelineX, timelineY, timelineWidth * progress, 4, 2);
    ctx.fill();

    // Time indicator
    ctx.font = '500 10px system-ui';
    ctx.fillStyle = 'rgba(255, 255, 255, 0.6)';
    ctx.textAlign = 'left';
    ctx.fillText(`${(currentFrame.frameId / 10).toFixed(1)}s`, timelineX, timelineY - 6);
    ctx.textAlign = 'right';
    ctx.fillText(`${(currentPlay.frames[currentPlay.frames.length - 1].frameId / 10).toFixed(1)}s`, timelineX + timelineWidth, timelineY - 6);

  }, [frame, currentPlayIndex, filteredPlays]);

  const handlePlayPause = () => setIsPlaying(!isPlaying);
  const handleReset = () => { setFrame(0); setIsPlaying(false); };
  const handlePrevPlay = () => {
    if (currentPlayIndex > 0) {
      setCurrentPlayIndex(currentPlayIndex - 1);
      setFrame(0);
      setIsPlaying(false);
    }
  };
  const handleNextPlay = () => {
    if (currentPlayIndex < filteredPlays.length - 1) {
      setCurrentPlayIndex(currentPlayIndex + 1);
      setFrame(0);
      setIsPlaying(false);
    }
  };

  const startRecording = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    recordedChunksRef.current = [];

    const stream = canvas.captureStream(30);
    const mediaRecorder = new MediaRecorder(stream, {
      mimeType: 'video/webm;codecs=vp9',
      videoBitsPerSecond: 2500000
    });

    mediaRecorder.ondataavailable = (event) => {
      if (event.data.size > 0) {
        recordedChunksRef.current.push(event.data);
      }
    };

    mediaRecorder.onstop = () => {
      const blob = new Blob(recordedChunksRef.current, { type: 'video/webm' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      const currentPlay = filteredPlays[currentPlayIndex];
      a.download = `play_${currentPlay.gameId}_${currentPlay.playId}.webm`;
      a.click();
      URL.revokeObjectURL(url);
      setIsRecording(false);
    };

    mediaRecorder.start();
    mediaRecorderRef.current = mediaRecorder;
    setIsRecording(true);
    setIsPlaying(true);
    setFrame(0);
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsPlaying(false);
    }
  };

  useEffect(() => {
    if (isRecording && filteredPlays.length > 0) {
      const currentPlay = filteredPlays[currentPlayIndex];
      if (frame >= currentPlay.frames.length - 1) {
        stopRecording();
      }
    }
  }, [frame, isRecording, currentPlayIndex, filteredPlays]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-[#0a0a0a]">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-blue-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <div className="text-white/60 text-sm tracking-wide">LOADING TRACKING DATA</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center h-screen bg-[#0a0a0a] text-white">
        <div className="text-center max-w-md">
          <div className="text-red-400 text-sm tracking-wide mb-2">ERROR</div>
          <div className="text-white/60 text-sm mb-6">{error}</div>
          <div className="text-xs text-white/40 text-left bg-white/5 p-4 rounded-lg font-mono">
            <p className="mb-2">Required files:</p>
            <code className="block">public/data/input_all.csv</code>
            <code className="block">public/data/output_all.csv</code>
            <code className="block">public/data/supplementary_data.csv</code>
          </div>
        </div>
      </div>
    );
  }

  if (filteredPlays.length === 0) {
    return (
      <div className="flex items-center justify-center h-screen bg-[#0a0a0a] text-white/60">
        No plays found
      </div>
    );
  }

  const currentPlay = filteredPlays[currentPlayIndex];

  return (
    <div className="min-h-screen bg-[#0a0a0a] text-white">
      {/* Header */}
      <div className="border-b border-white/10">
        <div className="max-w-5xl mx-auto px-6 py-4 flex items-center justify-between">
          <div>
            <h1 className="text-lg font-semibold tracking-tight">Play Analyzer</h1>
            <p className="text-xs text-white/40 tracking-wide">SEPARATION TRACKING</p>
          </div>

          <select
            value={selectedGame || ''}
            onChange={(e) => setSelectedGame(e.target.value)}
            className="bg-white/5 border border-white/10 rounded-lg px-4 py-2 text-sm text-white/80 focus:outline-none focus:border-blue-500/50 transition-colors"
          >
            {games.map(game => (
              <option key={game.gameId} value={game.gameId} className="bg-[#1a1a1a]">
                Game {game.gameId}
              </option>
            ))}
          </select>
        </div>
      </div>

      <div className="max-w-5xl mx-auto px-6 py-6">
        {/* Play Navigation */}
        <div className="flex items-center justify-between mb-4">
          <button
            onClick={handlePrevPlay}
            disabled={currentPlayIndex === 0}
            className="flex items-center gap-2 px-3 py-2 text-sm text-white/60 hover:text-white disabled:opacity-30 disabled:cursor-not-allowed transition-colors"
          >
            <ChevronLeft className="w-4 h-4" />
            Previous
          </button>

          <div className="text-center">
            <div className="text-xs text-white/40 tracking-widest mb-1">PLAY</div>
            <div className="text-lg font-semibold tabular-nums">
              {currentPlayIndex + 1} <span className="text-white/30">/ {filteredPlays.length}</span>
            </div>
          </div>

          <button
            onClick={handleNextPlay}
            disabled={currentPlayIndex === filteredPlays.length - 1}
            className="flex items-center gap-2 px-3 py-2 text-sm text-white/60 hover:text-white disabled:opacity-30 disabled:cursor-not-allowed transition-colors"
          >
            Next
            <ChevronRight className="w-4 h-4" />
          </button>
        </div>

        {/* Canvas Container */}
        <div className="relative rounded-xl overflow-hidden shadow-2xl shadow-black/50 mb-6">
          <canvas
            ref={canvasRef}
            width={800}
            height={480}
            className="w-full block"
          />
        </div>

        {/* Controls */}
        <div className="bg-white/5 rounded-xl p-4 border border-white/5">
          <div className="flex items-center gap-4">
            {/* Playback controls */}
            <div className="flex items-center gap-2">
              <button
                onClick={handleReset}
                className="w-10 h-10 flex items-center justify-center rounded-lg bg-white/5 hover:bg-white/10 transition-colors"
                disabled={isRecording}
              >
                <SkipBack className="w-4 h-4" />
              </button>
              <button
                onClick={handlePlayPause}
                className="w-12 h-12 flex items-center justify-center rounded-xl bg-blue-500 hover:bg-blue-400 transition-colors"
                disabled={isRecording}
              >
                {isPlaying ? <Pause className="w-5 h-5" /> : <Play className="w-5 h-5 ml-0.5" />}
              </button>

              {!isRecording ? (
                <button
                  onClick={startRecording}
                  className="w-10 h-10 flex items-center justify-center rounded-lg bg-white/5 hover:bg-red-500/20 hover:text-red-400 transition-colors"
                  title="Record"
                >
                  <Download className="w-4 h-4" />
                </button>
              ) : (
                <button
                  onClick={stopRecording}
                  className="w-10 h-10 flex items-center justify-center rounded-lg bg-red-500/20 text-red-400"
                >
                  <div className="w-3 h-3 bg-red-400 rounded-sm animate-pulse" />
                </button>
              )}
            </div>

            {/* Timeline */}
            <div className="flex-1 flex items-center gap-3">
              <input
                type="range"
                min="0"
                max={currentPlay.frames.length - 1}
                value={frame}
                onChange={(e) => setFrame(parseInt(e.target.value))}
                className="flex-1 h-1 appearance-none bg-white/10 rounded-full cursor-pointer [&::-webkit-slider-thumb]:appearance-none [&::-webkit-slider-thumb]:w-3 [&::-webkit-slider-thumb]:h-3 [&::-webkit-slider-thumb]:rounded-full [&::-webkit-slider-thumb]:bg-blue-500"
                disabled={isRecording}
              />
              <span className="text-xs text-white/40 tabular-nums w-12 text-right">
                {(currentPlay.frames[frame]?.frameId / 10 || 0).toFixed(1)}s
              </span>
            </div>

            {/* Speed control */}
            <div className="flex items-center gap-2 pl-4 border-l border-white/10">
              <span className="text-xs text-white/40">Speed</span>
              <input
                type="range"
                min="50"
                max="300"
                step="50"
                value={playSpeed}
                onChange={(e) => setPlaySpeed(parseInt(e.target.value))}
                className="w-20 h-1 appearance-none bg-white/10 rounded-full cursor-pointer [&::-webkit-slider-thumb]:appearance-none [&::-webkit-slider-thumb]:w-2 [&::-webkit-slider-thumb]:h-2 [&::-webkit-slider-thumb]:rounded-full [&::-webkit-slider-thumb]:bg-white/60"
                disabled={isRecording}
              />
              <span className="text-xs text-white/60 tabular-nums w-8">{(1000/playSpeed).toFixed(0)}x</span>
            </div>
          </div>

          {isRecording && (
            <div className="text-xs text-red-400/80 text-center mt-3 animate-pulse">
              Recording  will auto-stop when complete
            </div>
          )}
        </div>

        {/* Legend */}
        <div className="flex items-center justify-center gap-8 mt-6 text-xs text-white/40">
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 rounded-full bg-blue-500 shadow-lg shadow-blue-500/30"></div>
            <span>Target WR</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 rounded-full bg-red-500 shadow-lg shadow-red-500/30"></div>
            <span>Coverage</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 rounded-full bg-emerald-500 shadow-lg shadow-emerald-500/30"></div>
            <span>Passer</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-6 h-0.5 bg-gradient-to-r from-blue-500 to-red-500 rounded"></div>
            <span>Separation</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NFLPlayVisualizer;
