import React, { useEffect, useState, useMemo, useRef } from 'react';

// --- Utility Functions ---
const normalizeName = (name) => {
  if (!name) return '';
  return name
    .toLowerCase()
    .replace(/\./g, '')        
    .replace(/[^a-z\s-]/g, '') 
    .replace(/\s+/g, '-');     
};

const useTransitionEffect = (targetValue, duration = 500) => {
  const [currentValue, setCurrentValue] = useState(targetValue);
  const animationRef = useRef();

  useEffect(() => {
    if (isNaN(targetValue)) {
      setCurrentValue(targetValue);
      return;
    }

    cancelAnimationFrame(animationRef.current);
    const startValue = currentValue;
    const startTime = performance.now();

    const animate = (time) => {
      const elapsed = time - startTime;
      const progress = Math.min(1, elapsed / duration);
      const easedProgress = progress < 0.5 
        ? 2 * progress * progress 
        : -1 + (4 - 2 * progress) * progress;
      const newValue = startValue + (targetValue - startValue) * easedProgress;
      setCurrentValue(newValue);
      if (progress < 1) {
        animationRef.current = requestAnimationFrame(animate);
      }
    };

    animationRef.current = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationRef.current);
  }, [targetValue, duration]);

  return currentValue;
};

// --- Player Node Component ---
const PlayerNode = React.memo(({ d, xPos, yPos, hovered, setHovered, isVisible }) => {
  const cleanId = `clip-${normalizeName(d.player)}`;
  const initials = d.player.split(' ').map(n => n[0]).join('');

  const animatedRadius = useTransitionEffect(d.radius);
  const animatedDiameter = animatedRadius * 2;
  const strokeColor = d.wos > d.exp ? '#10b981' : '#ef4444';

  return (
    <g 
      onMouseEnter={() => setHovered(d)}
      onMouseLeave={() => setHovered(null)}
      className="cursor-pointer"
      style={{ 
        opacity: !isVisible ? 0.06 : (hovered ? (hovered.player !== d.player ? 0.3 : 1) : 1),
        transition: 'opacity 0.6s ease',
        filter: !isVisible ? 'grayscale(100%)' : 'none'
      }}
    >
      <defs>
        <clipPath id={cleanId}>
          <circle cx={xPos} cy={yPos} r={animatedRadius} />
        </clipPath>
      </defs>

      <circle cx={xPos} cy={yPos} r={animatedRadius} fill="#b8b8b8" />
      <text 
        x={xPos} y={yPos + 4} textAnchor="middle" 
        fontSize={animatedRadius * 0.8} fontWeight="bold" fill="#444" 
        style={{ pointerEvents: 'none' }} 
      >
        {initials}
      </text>

      <image 
        xlinkHref={d.img} 
        x={xPos - animatedRadius} 
        y={yPos - animatedRadius} 
        width={animatedDiameter} 
        height={animatedDiameter} 
        preserveAspectRatio="xMidYMid slice"
        clipPath={`url(#${cleanId})`}
        onError={(e) => { e.target.style.display = 'none'; }}
      />

      <circle 
        cx={xPos} cy={yPos} r={animatedRadius} fill="none" 
        stroke={strokeColor} 
        strokeWidth={3}
        filter="url(#dropShadow)"
      />

      <text
        x={xPos}
        y={yPos + animatedRadius + 12} 
        textAnchor="middle"
        fontSize="10" 
        fontWeight="600"
        fill="#333"
        className="pointer-events-none"
      >
        {d.player}
      </text>
    </g>
  );
});


const RouteComparisonVisualizer = () => {
  const [data, setData] = useState([]);
  const [hovered, setHovered] = useState(null);
  const [minTargets, setMinTargets] = useState(50);

  // Animation state
  const [activeFeature, setActiveFeature] = useState(null); // 'ttt' | 'def_speed' | null
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTTT, setCurrentTTT] = useState(3.5);
  const [currentDefSpeed, setCurrentDefSpeed] = useState(1.3);

  // Record mode state
  const [recordMode, setRecordMode] = useState(false);
  const [countdown, setCountdown] = useState(null);
  const [titleCard, setTitleCard] = useState(null); // { title, subtitle, color }

  // Data ranges
  const [dataRanges, setDataRanges] = useState({ 
    tttMin: 2.5, tttMax: 3.5, 
    defMin: 1.3, defMax: 1.8 
  });

  const animationRef = useRef(null);
  const timeoutsRef = useRef([]);

  // Clear all timeouts helper
  const clearAllTimeouts = () => {
    timeoutsRef.current.forEach(t => clearTimeout(t));
    timeoutsRef.current = [];
  };

  // Add timeout helper
  const addTimeout = (fn, delay) => {
    const id = setTimeout(fn, delay);
    timeoutsRef.current.push(id);
    return id;
  };

  useEffect(() => {
    fetch('/data/elite_leaderboard3.csv')
      .then(res => res.text())
      .then(text => {
        const [header, ...rows] = text.trim().split('\n');
        const cols = header.split(',');

        const parsed = rows.map(r => {
          const v = r.split(',');
          const row = {};
          cols.forEach((c, i) => (row[c] = v[i]));
          if (!row.player_name) return null;

          return {
            player: row.player_name,
            wos: +row.Advanced_WOS,
            exp: +row.exp_sep_xgb,
            targets: +row.play_id,
            sep_at_arrival: +row.sep_at_arrival,
            pass_length: +row.pass_length,
            time_to_throw: +row.time_to_throw,
            def_avg_speed: +row.def_avg_speed,
            img: `/headshots/${normalizeName(row.player_name)}.jpg`
          };
        });

        const validData = parsed.filter(d => d && !isNaN(d.wos) && !isNaN(d.exp));

        // Calculate ranges
        const allTTT = validData.map(d => d.time_to_throw);
        const allDefSpeed = validData.map(d => d.def_avg_speed);

        const ranges = {
          tttMin: Math.floor(Math.min(...allTTT) * 100) / 100,
          tttMax: Math.ceil(Math.max(...allTTT) * 100) / 100,
          defMin: Math.floor(Math.min(...allDefSpeed) * 100) / 100,
          defMax: Math.ceil(Math.max(...allDefSpeed) * 100) / 100
        };

        setDataRanges(ranges);
        setCurrentTTT(ranges.tttMax);
        setCurrentDefSpeed(ranges.defMin);
        setData(validData);
      });
  }, []);

  // Reset function
  const resetFilters = () => {
    cancelAnimationFrame(animationRef.current);
    clearAllTimeouts();
    setIsPlaying(false);
    setActiveFeature(null);
    setCurrentTTT(dataRanges.tttMax);
    setCurrentDefSpeed(dataRanges.defMin);
    setTitleCard(null);
    setRecordMode(false);
    setCountdown(null);
  };

  // Play TTT Animation
  const playTTTAnimation = (duration = 8000) => {
    cancelAnimationFrame(animationRef.current);
    setActiveFeature('ttt');
    setIsPlaying(true);
    setCurrentTTT(dataRanges.tttMax);

    const startTime = performance.now();
    const startVal = dataRanges.tttMax;
    const endVal = dataRanges.tttMin;

    const animate = (time) => {
      const elapsed = time - startTime;
      const progress = Math.min(1, elapsed / duration);
      const newVal = startVal + (endVal - startVal) * progress;
      setCurrentTTT(newVal);

      if (progress < 1) {
        animationRef.current = requestAnimationFrame(animate);
      } else {
        setIsPlaying(false);
      }
    };

    animationRef.current = requestAnimationFrame(animate);
  };

  // Play Def Speed Animation
  const playDefSpeedAnimation = (duration = 8000) => {
    cancelAnimationFrame(animationRef.current);
    setActiveFeature('def_speed');
    setIsPlaying(true);
    setCurrentDefSpeed(dataRanges.defMin);

    const startTime = performance.now();
    const startVal = dataRanges.defMin;
    const endVal = dataRanges.defMax;

    const animate = (time) => {
      const elapsed = time - startTime;
      const progress = Math.min(1, elapsed / duration);
      const newVal = startVal + (endVal - startVal) * progress;
      setCurrentDefSpeed(newVal);

      if (progress < 1) {
        animationRef.current = requestAnimationFrame(animate);
      } else {
        setIsPlaying(false);
      }
    };

    animationRef.current = requestAnimationFrame(animate);
  };

  // Manual play buttons
  const playTTT = () => {
    resetFilters();
    playTTTAnimation(8000);
  };

  const playDefSpeed = () => {
    resetFilters();
    playDefSpeedAnimation(8000);
  };

  // ============================================
  // CINEMATIC AUTO-PLAY SEQUENCE
  // ============================================
  const playRecordMode = () => {
    resetFilters();
    setRecordMode(true);

    // Timeline (in milliseconds):
    // 0-3000: Countdown 3...2...1
    // 3000-7000: Title card for TTT (4 seconds)
    // 7000-15000: TTT animation (8 seconds)
    // 15000-19000: Hold on final state (4 seconds)
    // 19000-20000: Reset pause
    // 20000-24000: Title card for Def Speed (4 seconds)
    // 24000-32000: Def Speed animation (8 seconds)
    // 32000-36000: Hold on final state (4 seconds)
    // 36000: Done

    // Countdown
    setCountdown(3);
    addTimeout(() => setCountdown(2), 1000);
    addTimeout(() => setCountdown(1), 2000);
    addTimeout(() => setCountdown(null), 3000);

    // TTT Title Card
    addTimeout(() => {
      setTitleCard({
        title: 'TIME TO THROW',
        subtitle: 'Which receivers get open when the QB has no time?',
        description: 'As time decreases, players who only get open with long developing plays will fade out.',
        color: '#2563eb'
      });
    }, 3000);

    // Start TTT Animation
    addTimeout(() => {
      setTitleCard(null);
      playTTTAnimation(8000);
    }, 7000);

    // Hold after TTT (animation ends at 15000, hold until 19000)

    // Reset for next animation
    addTimeout(() => {
      setActiveFeature(null);
      setCurrentTTT(dataRanges.tttMax);
      setCurrentDefSpeed(dataRanges.defMin);
    }, 19000);

    // Def Speed Title Card
    addTimeout(() => {
      setTitleCard({
        title: 'DEFENDER SPEED',
        subtitle: 'Which receivers beat elite coverage?',
        description: 'As defender speed increases, only players who separate against the fastest DBs remain.',
        color: '#dc2626'
      });
    }, 20000);

    // Start Def Speed Animation
    addTimeout(() => {
      setTitleCard(null);
      playDefSpeedAnimation(8000);
    }, 24000);

    // Hold after Def Speed (animation ends at 32000, hold until 36000)

    // Done
    addTimeout(() => {
      setRecordMode(false);
      setIsPlaying(false);
    }, 36000);
  };

  // Determine visibility for each player
  const getPlayerVisibility = (player) => {
    if (activeFeature === 'ttt') {
      return player.time_to_throw <= currentTTT;
    }
    if (activeFeature === 'def_speed') {
      return player.def_avg_speed >= currentDefSpeed;
    }
    return true;
  };

  const processedData = useMemo(() => {
    if (!data.length) return [];
    let filtered = data.filter(d => d.targets >= minTargets);
    return filtered.map(d => ({
      ...d,
      radius: 16
    }));
  }, [data, minTargets]);

  // Count visible players
  const visibleCount = useMemo(() => {
    return processedData.filter(d => getPlayerVisibility(d)).length;
  }, [processedData, activeFeature, currentTTT, currentDefSpeed]);

  // Count visible players who beat expectation
  const visibleCreators = useMemo(() => {
    return processedData.filter(d => getPlayerVisibility(d) && d.wos > d.exp).length;
  }, [processedData, activeFeature, currentTTT, currentDefSpeed]);

  if (!data.length) return <div className="p-10 text-center font-serif text-gray-500">Loading Chart...</div>;

  const W = 1200; 
  const H = 750;  
  const P = 100; 

  const xs = data.map(d => d.wos);
  const ys = data.map(d => d.exp);
  const xMin = Math.min(...xs) - 0.1;
  const xMax = Math.max(...xs) + 0.1;
  const yMin = Math.min(...ys) - 0.1;
  const yMax = Math.max(...ys) + 0.1;
  const avgX = xs.reduce((a, b) => a + b, 0) / xs.length;
  const avgY = ys.reduce((a, b) => a + b, 0) / ys.length;

  const xScale = v => P + ((v - xMin) / (xMax - xMin)) * (W - P * 2);
  const yScale = v => H - P - ((v - yMin) / (yMax - yMin)) * (H - P * 2);

  return (
    <div className="flex flex-col items-center bg-[#f4f1ea] p-6 rounded-xl shadow-2xl max-w-[1600px] mx-auto font-serif relative">

      {/* ============ COUNTDOWN OVERLAY ============ */}
      {countdown && (
        <div className="fixed inset-0 bg-black/80 flex items-center justify-center z-50">
          <div className="text-white text-center">
            <div 
              className="text-[12rem] font-black leading-none"
              style={{ 
                fontFamily: "'Georgia', serif",
                textShadow: '0 0 60px rgba(255,255,255,0.5)',
                animation: 'pulse 1s ease-in-out'
              }}
            >
              {countdown}
            </div>
            <div className="text-2xl mt-8 uppercase tracking-[0.3em] text-gray-400">
              Starting Recording
            </div>
          </div>
        </div>
      )}

      {/* ============ TITLE CARD OVERLAY ============ */}
      {titleCard && (
        <div className="fixed inset-0 bg-black/85 flex items-center justify-center z-50">
          <div className="text-center max-w-3xl px-8">
            <div 
              className="text-7xl font-black tracking-wider mb-6"
              style={{ 
                fontFamily: "'Georgia', serif",
                color: titleCard.color,
                textShadow: `0 0 40px ${titleCard.color}60`
              }}
            >
              {titleCard.title}
            </div>
            <div className="text-3xl text-white font-light mb-8">
              {titleCard.subtitle}
            </div>
            <div className="text-xl text-gray-400 leading-relaxed">
              {titleCard.description}
            </div>
          </div>
        </div>
      )}

      {/* ============ HEADER ============ */}
      <div className="mb-4 text-center">
        <h2 className="text-3xl font-bold text-[#2c2c2c] uppercase tracking-wider">
          Who Creates Separation in the Hardest Situations?
        </h2>
        <p className="text-[#666] italic mt-1">
          Watch as we filter out "easy mode" - the survivors are the true creators
        </p>
      </div>

      {/* ============ CONTROL BUTTONS ============ */}
      <div className="flex gap-4 mb-6">
        <button
          onClick={playRecordMode}
          disabled={isPlaying || recordMode}
          className={`px-8 py-4 rounded-xl font-bold uppercase tracking-wider transition-all duration-300
                      ${recordMode 
                        ? 'bg-red-600 text-white shadow-lg shadow-red-300 animate-pulse' 
                        : 'bg-gradient-to-r from-red-500 to-orange-500 text-white hover:scale-105 shadow-lg'}
                      ${isPlaying || recordMode ? 'cursor-not-allowed' : ''}`}
        >
           {recordMode ? 'Recording...' : 'Auto-Play (Record Mode)'}
        </button>

        <div className="w-px bg-gray-300" />

        <button
          onClick={playTTT}
          disabled={isPlaying || recordMode}
          className={`px-6 py-3 rounded-xl font-bold uppercase tracking-wider transition-all duration-300
                      ${activeFeature === 'ttt' && !titleCard
                        ? 'bg-blue-600 text-white shadow-lg shadow-blue-300 scale-105' 
                        : 'bg-white text-blue-600 border-2 border-blue-300 hover:bg-blue-50'}
                      ${isPlaying || recordMode ? 'opacity-50 cursor-not-allowed' : 'hover:scale-105'}`}
        >
           Time to Throw
        </button>

        <button
          onClick={playDefSpeed}
          disabled={isPlaying || recordMode}
          className={`px-6 py-3 rounded-xl font-bold uppercase tracking-wider transition-all duration-300
                      ${activeFeature === 'def_speed' && !titleCard
                        ? 'bg-red-600 text-white shadow-lg shadow-red-300 scale-105' 
                        : 'bg-white text-red-600 border-2 border-red-300 hover:bg-red-50'}
                      ${isPlaying || recordMode ? 'opacity-50 cursor-not-allowed' : 'hover:scale-105'}`}
        >
           Defender Speed
        </button>

        <button
          onClick={resetFilters}
          disabled={recordMode}
          className="px-6 py-3 rounded-xl font-bold uppercase tracking-wider bg-gray-200 text-gray-600 hover:bg-gray-300 transition-all"
        >
           Reset
        </button>
      </div>

      {/* ============ MAIN CONTENT ============ */}
      <div className="flex gap-6 items-start">

        {/* ============ LEFT: BIG TTT DISPLAY ============ */}
        <div className={`flex flex-col items-center transition-all duration-500 w-[180px]
                         ${activeFeature === 'ttt' ? 'opacity-100 scale-105' : 'opacity-20'}`}>
          <div className="text-sm font-bold text-blue-600 uppercase tracking-wider mb-2">
            Time to Throw
          </div>
          <div 
            className="text-6xl font-black font-mono text-blue-600 tabular-nums"
            style={{ 
              fontFamily: "'Georgia', serif",
              textShadow: activeFeature === 'ttt' ? '0 0 20px rgba(37, 99, 235, 0.5)' : 'none'
            }}
          >
            {currentTTT.toFixed(2)}
          </div>
          <div className="text-lg text-blue-400 font-medium">seconds</div>

          {/* Progress bar */}
          <div className="w-full h-3 bg-gray-200 rounded-full mt-4 overflow-hidden">
            <div 
              className="h-full bg-blue-500 rounded-full transition-all duration-100"
              style={{ 
                width: `${((dataRanges.tttMax - currentTTT) / (dataRanges.tttMax - dataRanges.tttMin)) * 100}%`
              }}
            />
          </div>
          <div className="flex justify-between w-full text-xs text-gray-400 mt-1">
            <span>{dataRanges.tttMax.toFixed(1)}s</span>
            <span>{dataRanges.tttMin.toFixed(1)}s</span>
          </div>

          {activeFeature === 'ttt' && (
            <div className="mt-4 text-center text-sm text-blue-700 font-medium px-2">
               Filtering out receivers who need more than {currentTTT.toFixed(2)}s
            </div>
          )}
        </div>

        {/* ============ CENTER: CHART ============ */}
        <div className="relative">
          <svg width={W} height={H} className="border border-[#dcd9d0] bg-[#f9f7f1] rounded-lg">
            <defs>
              <filter id="dropShadow" x="-20%" y="-20%" width="140%" height="140%">
                <feDropShadow dx="2" dy="2" stdDeviation="2" floodColor="#000" floodOpacity="0.25" />
              </filter>
            </defs>

            {/* Quadrant shading */}
            <rect 
              x={xScale(avgX)} y={P} 
              width={W - P - xScale(avgX)} height={yScale(avgY) - P}
              fill="#10b98110"
            />
            <rect 
              x={P} y={yScale(avgY)} 
              width={xScale(avgX) - P} height={H - P - yScale(avgY)}
              fill="#ef444410"
            />

            {/* Axis Labels */}
            <text x={W/2} y={H - 30} textAnchor="middle" fontWeight="bold" fill="#333" fontSize="14">
              Created Separation (WOS) 
            </text>
            <text x={25} y={H/2} textAnchor="middle" fontWeight="bold" fill="#333" fontSize="14" transform={`rotate(-90, 25, ${H/2})`}>
              Expected Separation 
            </text>

            {/* Grid lines */}
            <line x1={xScale(avgX)} y1={P} x2={xScale(avgX)} y2={H-P} stroke="#999" strokeWidth="1" strokeDasharray="4,4" />
            <line x1={P} y1={yScale(avgY)} x2={W-P} y2={yScale(avgY)} stroke="#999" strokeWidth="1" strokeDasharray="4,4" />

            {/* Quadrant labels */}
            <text x={W - P - 10} y={P + 25} textAnchor="end" fontSize="14" fill="#10b981" fontWeight="bold">CREATORS </text>
            <text x={P + 10} y={H - P - 10} textAnchor="start" fontSize="14" fill="#ef4444" fontWeight="bold">UNDERPERFORMERS</text>

            {/* Axis ticks */}
            {[xMin, avgX, xMax].map((v,i) => (
              <text key={i} x={xScale(v)} y={H-P+18} textAnchor="middle" fill="#666" fontSize="11">{v.toFixed(2)}</text>
            ))}
            {[yMin, avgY, yMax].map((v,i) => (
              <text key={i} x={P-8} y={yScale(v)} textAnchor="end" fill="#666" fontSize="11" dy="0.3em">{v.toFixed(2)}</text>
            ))}

            {/* Player Nodes */}
            {processedData.map(d => (
              <PlayerNode
                key={d.player}
                d={d}
                xPos={xScale(d.wos)}
                yPos={yScale(d.exp)}
                hovered={hovered}
                setHovered={setHovered}
                isVisible={getPlayerVisibility(d)}
              />
            ))}
          </svg>

          {/* Tooltip */}
          {hovered && !titleCard && !countdown && (
            <div 
              className="absolute z-40 bg-white/95 backdrop-blur border border-stone-300 shadow-xl p-4 rounded-lg min-w-[240px] pointer-events-none"
              style={{ left: xScale(hovered.wos) + 25, top: yScale(hovered.exp) - 30 }}
            >
              <div className="font-bold text-lg text-stone-800 border-b pb-1 mb-2">{hovered.player}</div>
              <div className="grid grid-cols-2 gap-x-3 text-sm">
                <span className="text-gray-500">Expected Sep:</span> 
                <span className="text-right font-bold">{hovered.exp.toFixed(2)}</span>
                <span className="text-gray-500">Created Sep:</span> 
                <span className={`text-right font-bold ${hovered.wos > hovered.exp ? 'text-emerald-600' : 'text-red-500'}`}>
                  {hovered.wos.toFixed(2)}
                </span>
                <span className="text-blue-600 font-semibold">Time to Throw:</span> 
                <span className="text-right font-bold text-blue-600">{hovered.time_to_throw.toFixed(2)}s</span>
                <span className="text-red-600 font-semibold">Def Speed:</span> 
                <span className="text-right font-bold text-red-600">{hovered.def_avg_speed.toFixed(2)} mph</span>
              </div>
            </div>
          )}

          {/* Stats overlay */}
          <div className="absolute top-4 left-4 bg-white/90 backdrop-blur rounded-lg p-4 shadow-md">
            <div className="text-sm text-gray-600">
              <span className="font-bold text-2xl text-gray-800">{visibleCount}</span> 
              <span className="ml-1">players visible</span>
            </div>
            <div className="text-sm mt-1">
              <span className="font-bold text-2xl text-emerald-600">{visibleCreators}</span> 
              <span className="text-gray-600 ml-1">beat expectation</span>
            </div>
          </div>
        </div>

        {/* ============ RIGHT: BIG DEF SPEED DISPLAY ============ */}
        <div className={`flex flex-col items-center transition-all duration-500 w-[180px]
                         ${activeFeature === 'def_speed' ? 'opacity-100 scale-105' : 'opacity-20'}`}>
          <div className="text-sm font-bold text-red-600 uppercase tracking-wider mb-2">
            Defender Speed
          </div>
          <div 
            className="text-6xl font-black font-mono text-red-600 tabular-nums"
            style={{ 
              fontFamily: "'Georgia', serif",
              textShadow: activeFeature === 'def_speed' ? '0 0 20px rgba(220, 38, 38, 0.5)' : 'none'
            }}
          >
            {currentDefSpeed.toFixed(2)}
          </div>
          <div className="text-lg text-red-400 font-medium">mph</div>

          {/* Progress bar */}
          <div className="w-full h-3 bg-gray-200 rounded-full mt-4 overflow-hidden">
            <div 
              className="h-full bg-red-500 rounded-full transition-all duration-100"
              style={{ 
                width: `${((currentDefSpeed - dataRanges.defMin) / (dataRanges.defMax - dataRanges.defMin)) * 100}%`
              }}
            />
          </div>
          <div className="flex justify-between w-full text-xs text-gray-400 mt-1">
            <span>{dataRanges.defMin.toFixed(1)}</span>
            <span>{dataRanges.defMax.toFixed(1)}</span>
          </div>

          {activeFeature === 'def_speed' && (
            <div className="mt-4 text-center text-sm text-red-700 font-medium px-2">
               Filtering out receivers facing defenders slower than {currentDefSpeed.toFixed(2)} mph
            </div>
          )}
        </div>
      </div>

      {/* ============ BOTTOM INSIGHT BAR ============ */}
      <div className={`mt-6 px-8 py-5 rounded-xl transition-all duration-500 max-w-[1000px] text-center
                       ${activeFeature === 'ttt' ? 'bg-blue-50 border-2 border-blue-300' : 
                         activeFeature === 'def_speed' ? 'bg-red-50 border-2 border-red-300' : 
                         'bg-gray-100 border border-gray-200'}`}>
        {activeFeature === 'ttt' && (
          <p className="text-blue-800 text-lg">
            <strong> Quick Release Test:</strong> Players visible can create separation even with a {currentTTT.toFixed(2)}s release. 
            <span className="block mt-2 text-blue-600">
              {visibleCreators} of {visibleCount} remaining players beat their expected separation.
            </span>
          </p>
        )}
        {activeFeature === 'def_speed' && (
          <p className="text-red-800 text-lg">
            <strong> Elite Coverage Test:</strong> Players visible are creating separation against defenders running {currentDefSpeed.toFixed(2)}+ mph.
            <span className="block mt-2 text-red-600">
              {visibleCreators} of {visibleCount} remaining players beat their expected separation.
            </span>
          </p>
        )}
        {!activeFeature && (
          <p className="text-gray-600 text-lg">
            Select a test above to see which receivers create separation in the most difficult circumstances.
          </p>
        )}
      </div>

      {/* ============ CSS ANIMATIONS ============ */}
      <style>{`
        @keyframes pulse {
          0%, 100% { transform: scale(1); opacity: 1; }
          50% { transform: scale(1.1); opacity: 0.8; }
        }
      `}</style>
    </div>
  );
};

export default RouteComparisonVisualizer;
