import React, { useEffect, useState, useRef } from 'react';
import * as d3 from 'd3';

// Utility for image paths
const normalizeName = (name) => {
  if (!name) return '';
  return name.toLowerCase().replace(/\./g, '').replace(/[^a-z\s-]/g, '').replace(/\s+/g, '-');
};

const W = 1600; 
const H = 900;  
const P = 120; // Padding

// NOTE: This component assumes advanced_wr_data.csv is accessible via the provided path.
const WRSeparationTraceMap = ({ dataFile = 'advanced_wr_data.csv' }) => {
  const [traces, setTraces] = useState({}); // Stores the full trace data grouped by player
  const [aggData, setAggData] = useState([]); // Stores the final aggregate position (last point of trace)
  const [animationFrame, setAnimationFrame] = useState(0); // Current frame/target index for animation
  const svgRef = useRef(null);

  // --- Data Loading and Processing (Handling the CSV internally) ---
  useEffect(() => {
    // We are loading the user's advanced_wr_data.csv here
    d3.csv(dataFile).then(data => {

      const parsedData = data.map(d => ({
        // Use the columns calculated in the Python step
        player: d.player_name, 
        wos: +d.wos_advanced,
        exp: +d.exp_sep_xgb,
        game_id: +d.game_id,
        play_id: +d.play_id,
      })).filter(d => d.player && !isNaN(d.wos) && !isNaN(d.exp));

      // 1. Sort the data by player, then by game/play ID to ensure correct chronological order
      const sortedData = parsedData.sort((a, b) => {
        if (a.player !== b.player) return a.player.localeCompare(b.player);
        if (a.game_id !== b.game_id) return a.game_id - b.game_id;
        return a.play_id - b.play_id;
      });

      // 2. Calculate running averages (the core of the trajectory)
      let currentTrace = {};
      let cumulativeSum = {};
      let targetCount = {};
      const finalAgg = [];

      sortedData.forEach(d => {
        const player = d.player;
        cumulativeSum[player] = (cumulativeSum[player] || { wos: 0, exp: 0 });
        targetCount[player] = (targetCount[player] || 0) + 1;

        cumulativeSum[player].wos += d.wos;
        cumulativeSum[player].exp += d.exp;

        const running_avg_wos = cumulativeSum[player].wos / targetCount[player];
        const running_avg_exp = cumulativeSum[player].exp / targetCount[player];

        const point = {
            player, 
            targets: targetCount[player], 
            wos: running_avg_wos, 
            exp: running_avg_exp
        };

        currentTrace[player] = (currentTrace[player] || []).concat(point);
      });

      // 3. Finalize data structures
      const groupedTraces = new Map(Object.entries(currentTrace));
      setTraces(groupedTraces);

      // Get the last data point (final aggregate) for each player
      Array.from(groupedTraces.values()).forEach(trace => {
          finalAgg.push(trace[trace.length - 1]);
      });

      setAggData(finalAgg);

      // Find the maximum number of targets to set the animation length
      const maxTargets = d3.max(finalAgg, d => d.targets);

      // 4. Start the animation timer
      let currentFrame = 1;
      const interval = setInterval(() => {
        currentFrame++;
        if (currentFrame > maxTargets) {
          clearInterval(interval);
        }
        setAnimationFrame(currentFrame);
      }, 50); // Speed of the animation (50ms per target/frame)

      return () => clearInterval(interval); // Cleanup
    });
  }, [dataFile]);

  if (aggData.length === 0) return <div className="p-10 text-center">Loading Separation Trajectories...</div>;

  // --- Scale and Axis Setup ---
  // ... (Scales and Axis logic remain the same, based on aggData) ...

  const xs = aggData.map(d => d.wos);
  const ys = aggData.map(d => d.exp);
  const maxTargets = d3.max(aggData, d => d.targets);

  const xScale = d3.scaleLinear()
    .domain(d3.extent(xs))
    .range([P, W - P])
    .nice();

  const yScale = d3.scaleLinear()
    .domain(d3.extent(ys))
    .range([H - P, P])
    .nice();

  // Function to get the current position (x, y) for a player at the current animation frame
  const getCurrentPosition = (player) => {
    const trace = traces.get(player);
    if (!trace) return null;

    // Find the latest point whose target_count is <= current frame
    let currentPoint = trace.filter(d => d.targets <= animationFrame).pop() || trace[0];

    if (!currentPoint) return null;

    return {
      x: xScale(currentPoint.wos),
      y: yScale(currentPoint.exp),
      targets: currentPoint.targets // Expose the current targets for labeling
    };
  };

  // D3 line generator for the trace path
  const lineGenerator = d3.line()
    .x(d => xScale(d.wos))
    .y(d => yScale(d.exp));

  // D3 color scale (for player trace colors)
  const playerNames = Array.from(traces.keys()).sort();
  const colorScale = d3.scaleOrdinal()
    .domain(playerNames)
    .range(d3.schemeSet3); // A palette of 12 distinct colors

  // Axis lines/labels (simplified for code presentation)
  const labelSize = 24; 
  const labelColor = '#4a5568'; 
  const labelMargin = 25; 


  return (
    <div className="flex flex-col items-center bg-[#f4f1ea] p-8 rounded-xl shadow-2xl max-w-[1700px] mx-auto font-serif">
      <div className="mb-4 text-center">
        <h2 className="text-4xl font-bold text-[#2c2c2c] uppercase tracking-wider">Separation Trajectory Map: Consistency is Key</h2>
        <p className="text-[#666] italic mt-1">Movement shows performance history (cumulative average WOS vs. ExpSep)</p>
      </div>

      <div className="relative">
        <svg width={W} height={H} ref={svgRef} className="border border-[#dcd9d0] bg-[#f9f7f1]">

          {/* Axis Lines and Quadrants */}
          <line x1={P} y1={H - P} x2={W - P} y2={H - P} stroke="#444" />
          <line x1={P} y1={P} x2={P} y2={H - P} stroke="#444" />

          {/* Axes Centered at League Averages (estimated from final data) */}
          <line x1={xScale(d3.mean(xs))} y1={P} x2={xScale(d3.mean(xs))} y2={H-P} stroke="#999" strokeWidth="1" strokeDasharray="4,4" />
          <line x1={P} y1={yScale(d3.mean(ys))} x2={W-P} y2={yScale(d3.mean(ys))} stroke="#999" strokeWidth="1" strokeDasharray="4,4" />

          {/* Quadrant Labels */}
          <text x={W - P - labelMargin} y={P + labelSize + labelMargin} textAnchor="end" fontSize={labelSize} fill={labelColor} fontWeight="bold">Achievers</text>
          <text x={W - P - labelMargin} y={H - P - labelMargin} textAnchor="end" fontSize={labelSize} fill={labelColor} fontWeight="bold">Creators</text>

          {/* Animation Frame Counter */}
          <text x={W - P} y={P - 20} textAnchor="end" fontSize="24" fill="#888" fontWeight="bold">
             Targets Processed: {animationFrame} / {maxTargets}
          </text>

          {/* --- Player Traces (The history lines) --- */}
          {Array.from(traces.keys()).map(player => {
            const fullTrace = traces.get(player);
            // Filter the trace data to the points up to the current frame
            const visibleTrace = fullTrace.filter(d => d.targets <= animationFrame);

            if (visibleTrace.length < 2) return null; // Need at least two points to draw a line

            const color = colorScale(player);

            return (
              <g key={`trace-${player}`} opacity={0.6}>
                {/* The Path Line */}
                <path
                  d={lineGenerator(visibleTrace)}
                  fill="none"
                  stroke={color}
                  strokeWidth={3}
                  strokeLinejoin="round"
                  strokeLinecap="round"
                />
              </g>
            );
          })}

          {/* --- Player Headshots (The Moving Dots) --- */}
          {Array.from(traces.keys()).map(player => {
            const pos = getCurrentPosition(player);
            if (!pos) return null;

            // Only show the moving dots once a minimum number of targets are hit
            if (pos.targets < 5) return null; 

            // Calculate size based on final target volume (final target count)
            const finalData = aggData.find(d => d.player === player);
            const radius = Math.min(Math.sqrt(finalData.targets) * 0.4, 25); 
            const diameter = radius * 2;
            const cleanId = `clip-${normalizeName(player)}`;
            const color = colorScale(player);

            return (
              <g 
                key={`dot-${player}`}
                transform={`translate(${pos.x}, ${pos.y})`}
                style={{ 
                    transition: 'transform 0.05s linear', // Smooth movement 
                    // Set opacity based on its final quadrant (Achiever vs Underperformer)
                    opacity: finalData.wos > finalData.exp ? 1 : 0.8
                }} 
              >
                 <defs>
                  <clipPath id={cleanId}>
                    <circle cx={0} cy={0} r={radius} /> 
                  </clipPath>
                 </defs>

                 {/* Image and Border Circle */}
                 <image 
                  xlinkHref={`/headshots/${normalizeName(player)}.jpg`} 
                  x={-radius} y={-radius} width={diameter} height={diameter} 
                  preserveAspectRatio="xMidYMid slice" clipPath={`url(#${cleanId})`}
                  onError={(e) => { e.target.style.display = 'none'; }}
                />

                <circle cx={0} cy={0} r={radius} fill="none" stroke={color} strokeWidth={4} />

                {/* Final Player Label (only appear on final frame/after the majority of movement) */}
                {pos.targets > maxTargets * 0.8 && (
                    <text
                        x={radius + 5} y={4} 
                        fontSize="14" fontWeight="bold" 
                        fill="#333"
                        textAnchor="start"
                    >
                        {player}
                    </text>
                )}
              </g>
            );
          })}

          {/* Axis Labels */}
          <text x={W / 2} y={H - 40} textAnchor="middle" fontWeight="bold" fill="#333" className="uppercase tracking-widest text-lg">Created Separation (WOS)</text>
          <text x={40} y={H / 2} textAnchor="middle" fontWeight="bold" fill="#333" className="uppercase tracking-widest text-lg" transform={`rotate(-90, 40, ${H / 2})`}>Expected Separation</text>


        </svg>
      </div>
      <div className="w-full text-center mt-4 text-sm text-stone-600 font-mono">
         X-axis: Running Average Created Separation (WOS) â€¢ Y-axis: Running Average Expected Separation
      </div>
    </div>
  );
};

