import React, { useEffect, useState } from 'react';
import * as d3 from 'd3';

// Utility for image paths
const normalizeName = (name) => {
  if (!name) return '';
  return name.toLowerCase().replace(/\./g, '').replace(/[^a-z\s-]/g, '').replace(/\s+/g, '-');
};

const W = 1600; 
const H = 900;  
const P = 120;  

// Hardcoding the calculated league averages for filter logic
// Based on the 'elite_leaderboard (2).csv' data analysis:
const AVG_PASS_LENGTH = 12.17;
const AVG_TIME_TO_THROW = 2.75;
const dataFile = 'wr_context_aggregated.csv';

const WRSeparationTraceMap = () => {
  const [data, setData] = useState([]);
  const [hovered, setHovered] = useState(null);

  // Controls the contextual filter: 'All', 'HighDifficulty', 'LowDifficulty'
  const [contextFilter, setContextFilter] = useState('All'); 

  useEffect(() => {
    // Load the prepared aggregated data
    d3.csv(dataFile).then(loadedData => {

        const parsed = loadedData.map(d => {
            if (!d.player_name) return null;

            return {
                player: d.player_name,
                wos: +d.wos_advanced,      // X-axis (Created Separation)
                exp: +d.exp_wos,           // Y-axis (Expected Separation)
                targets: +d.targets,
                avgPassLength: +d.avg_pass_length, 
                avgTimeToThrow: +d.avg_time_to_throw,
                img: `/headshots/${normalizeName(d.player_name)}.jpg`
            };
        }).filter(d => d && !isNaN(d.wos) && !isNaN(d.exp));

        setData(parsed);
    });
  }, []); 

  if (!data.length) return <div className="p-10 text-center font-serif text-gray-500">Loading Chart...</div>;

  /* ----- Config and Scales ----- */
  const xs = data.map(d => d.wos);
  const ys = data.map(d => d.exp);

  const xScale = d3.scaleLinear().domain(d3.extent(xs)).range([P, W - P]).nice();
  const yScale = d3.scaleLinear().domain(d3.extent(ys)).range([H - P, P]).nice();

  const avgX = d3.mean(xs);
  const avgY = d3.mean(ys);

  const labelSize = 24; 
  const labelColor = '#4a5568'; 
  const labelMargin = 25; 

  // --- Filtering Logic Function ---
  const getPlayerEmphasis = (d) => {
    let opacity = 1;
    let fillColor = '#333'; // Default to gray/dark

    // A player is in a "High Difficulty" context if their average target is longer 
    // AND/OR thrown slower than the league average.
    const isHighDifficulty = 
        d.avgPassLength >= AVG_PASS_LENGTH || 
        d.avgTimeToThrow >= AVG_TIME_TO_THROW; 

    // Determine the player's primary role color based on filter
    if (contextFilter === 'HighDifficulty') {
        if (!isHighDifficulty) {
            opacity = 0.05; // Fade out players who don't face high difficulty
        } else {
            fillColor = '#800080'; // Emphasize High Difficulty in Purple
        }
    } else if (contextFilter === 'LowDifficulty') {
        if (isHighDifficulty) {
            opacity = 0.05; // Fade out players who face high difficulty
        } else {
            fillColor = '#008080'; // Emphasize Low Difficulty in Teal
        }
    }

    // Always use WOS vs EXP for the stroke color (The separation performance score)
    const isGood = d.wos > d.exp;
    const strokeColor = isGood ? '#10b981' : '#ef4444'; 

    return { opacity, strokeColor, fillColor, isGood };
  };

  return (
    <div className="flex flex-col items-center bg-[#f4f1ea] p-8 rounded-xl shadow-2xl max-w-[1700px] mx-auto font-serif">

      {/* Header Section */}
      <div className="mb-4 text-center">
        <h2 className="text-4xl font-bold text-[#2c2c2c] uppercase tracking-wider">WR Separation Landscape: Contextual Difficulty</h2>
        <p className="text-[#666] italic mt-1">Separation vs. Expectation, filtered by Average Pass Length ({AVG_PASS_LENGTH} yd) and Time to Throw ({AVG_TIME_TO_THROW} sec)</p>
      </div>

      {/* Contextual Filter Buttons */}
      <div className="mb-6 flex gap-4">
        <button
          onClick={() => setContextFilter('All')}
          className={`px-5 py-2 text-md font-semibold rounded-full transition-all ${
            contextFilter === 'All' ? 'bg-stone-700 text-white' : 'bg-white text-stone-700 border border-stone-300 hover:bg-stone-100'
          }`}
        >
          Show All Players
        </button>
        <button
          onClick={() => setContextFilter('HighDifficulty')}
          className={`px-5 py-2 text-md font-semibold rounded-full transition-all ${
            contextFilter === 'HighDifficulty' ? 'bg-purple-700 text-white' : 'bg-white text-purple-700 border border-purple-300 hover:bg-purple-50'
          }`}
        >
          Highlight **High Difficulty** Context (Deep/Slow Throws)
        </button>
        <button
          onClick={() => setContextFilter('LowDifficulty')}
          className={`px-5 py-2 text-md font-semibold rounded-full transition-all ${
            contextFilter === 'LowDifficulty' ? 'bg-teal-700 text-white' : 'bg-white text-teal-700 border border-teal-300 hover:bg-teal-50'
          }`}
        >
          Highlight **Low Difficulty** Context (Short/Quick Throws)
        </button>
      </div>


      <div className="relative">
        <svg width={W} height={H} className="border border-[#dcd9d0] bg-[#f9f7f1]">

          {/* Axis Labels */}
          <text x={W/2} y={H - 40} textAnchor="middle" fontWeight="bold" fill="#333" className="uppercase tracking-widest text-lg">Created Separation (WOS)</text>
          <text x={40} y={H/2} textAnchor="middle" fontWeight="bold" fill="#333" className="uppercase tracking-widest text-lg" transform={`rotate(-90, 40, ${H/2})`}>Expected Separation</text>

          {/* Quadrant Labels */}
          <text x={W - P - labelMargin} y={P + labelSize + labelMargin} textAnchor="end" fontSize={labelSize} fill={labelColor} fontWeight="bold">The Achievers</text>
          <text x={W - P - labelMargin} y={H - P - labelMargin} textAnchor="end" fontSize={labelSize} fill="#10b981" fontWeight="bold">The Creators</text>

          {/* --- Diagonal 1:1 Reference Line --- */}
          <line 
            x1={xScale(xScale.domain()[0])} y1={yScale(yScale.domain()[0])} 
            x2={xScale(xScale.domain()[1])} y2={yScale(yScale.domain()[1])} 
            stroke="#999" strokeWidth="2" strokeDasharray="6,6" 
          />

          {/* --- Axes (Centered at Average) --- */}
          <line x1={xScale(avgX)} y1={P} x2={xScale(avgX)} y2={H-P} stroke="#444" strokeWidth="1" strokeDasharray="4,4" />
          <line x1={P} y1={yScale(avgY)} x2={W-P} y2={yScale(avgY)} stroke="#444" strokeWidth="1" strokeDasharray="4,4" />

          {/* Ticks */}
          {xScale.ticks(5).map((v,i) => (<text key={`x-${i}`} x={xScale(v)} y={H-P+25} textAnchor="middle" fill="#111" fontSize="14">{v.toFixed(2)}</text>))}
          {yScale.ticks(5).map((v,i) => (<text key={`y-${i}`} x={P-15} y={yScale(v)} textAnchor="end" fill="#111" fontSize="14" dy="0.3em">{v.toFixed(2)}</text>))}


          {/* --- Player Nodes and Names --- */}
          {data.map(d => {
            const radius = Math.min(Math.sqrt(d.targets) * 0.7, 25); 
            const diameter = radius * 2;
            const xPos = xScale(d.wos);
            const yPos = yScale(d.exp);

            const cleanId = `clip-${normalizeName(d.player)}`;
            const { opacity, strokeColor, fillColor } = getPlayerEmphasis(d);

            const textColor = opacity > 0.1 ? '#333' : '#aaa'; 
            const initials = d.player.split(' ').map(n => n[0]).join('');

            return (
              <g 
                key={d.player}
                onMouseEnter={() => setHovered(d)}
                onMouseLeave={() => setHovered(null)}
                className="cursor-pointer"
                style={{ 
                    opacity: opacity,
                    transition: 'opacity 0.5s ease-in-out' // Smooth fade
                }}
                transform={`translate(${xPos}, ${yPos})`} 
              >
                <defs>
                  <clipPath id={cleanId}>
                    <circle cx={0} cy={0} r={radius} /> 
                  </clipPath>
                </defs>

                {/* 1. FALLBACK CIRCLE & INITIALS */}
                <circle cx={0} cy={0} r={radius} fill={fillColor} />
                <text 
                    x={0} y={4} textAnchor="middle" 
                    fontSize={radius * 0.8} fontWeight="bold" fill="#fff" 
                    style={{ pointerEvents: 'none' }} 
                >
                    {initials}
                </text>

                {/* 2. The Image (Clipped by the circle) */}
                <image 
                  xlinkHref={d.img} 
                  x={-radius} y={-radius} width={diameter} height={diameter} 
                  preserveAspectRatio="xMidYMid slice"
                  clipPath={`url(#${cleanId})`}
                  onError={(e) => { e.target.style.display = 'none'; }}
                />

                {/* 3. The Border Circle (separation performance) */}
                <circle 
                  cx={0} cy={0} r={radius} fill="none" 
                  stroke={strokeColor} strokeWidth={3} 
                />

                {/* 4. PLAYER NAME LABEL */}
                <text
                  x={0} y={radius + 12} 
                  textAnchor="middle" fontSize="10" 
                  fontWeight="600" fill={textColor}
                  className="pointer-events-none"
                >
                  {d.player}
                </text>
              </g>
            );
          })}
        </svg>

        {/* --- Hover Tooltip --- */}
        {hovered && (
          <div 
            className="absolute z-50 bg-white/95 backdrop-blur border border-stone-300 shadow-xl p-4 rounded min-w-[250px] pointer-events-none"
            style={{ 
              left: xScale(hovered.wos) + 30, 
              top: yScale(hovered.exp) - 40 
            }}
          >
            <div className="font-bold text-lg text-stone-800 border-b pb-1 mb-2">{hovered.player}</div>
            <div className="grid grid-cols-2 gap-x-4 text-sm font-mono text-stone-600">
              <span>Targets:</span> <span className="text-right font-bold text-black">{hovered.targets}</span>
              <span>Avg Pass Length:</span> <span className="text-right">{hovered.avgPassLength.toFixed(2)} yd</span>
              <span>Avg Time to Throw:</span> <span className="text-right">{hovered.avgTimeToThrow.toFixed(2)} sec</span>
              <span className="mt-2">Expected Sep:</span> <span className="text-right mt-2">{hovered.exp.toFixed(2)}</span>
              <span>Created Sep:</span> <span className="text-right text-emerald-600 font-bold">{hovered.wos.toFixed(2)}</span>
            </div>
          </div>
        )}
      </div>
      <div className="w-full text-right mt-2 text-xs text-stone-400 font-mono">
         X/Y-axis: league avg â€¢ size: target share
      </div>
    </div>
  );
};

export default WRSeparationTraceMap;
