const handleFileUpload = (e) => {
  alert('Connect this to parse your parquet files with game_id, play_id, frame_id, x, y, player_role data');
};import React, { useState, useEffect, useRef } from 'react';
import { Play, Pause, SkipBack, SkipForward, Filter } from 'lucide-react';

const NFLPlayVisualizer = () => {
const [allPlays, setAllPlays] = useState([]);
const [games, setGames] = useState([]);
const [selectedGame, setSelectedGame] = useState(null);
const [filteredPlays, setFilteredPlays] = useState([]);
const [currentPlayIndex, setCurrentPlayIndex] = useState(0);
const [frame, setFrame] = useState(0);
const [isPlaying, setIsPlaying] = useState(false);
const [playSpeed, setPlaySpeed] = useState(100);
const canvasRef = useRef(null);

// Generate sample data with multiple games and plays
const generateSampleData = () => {
  const sampleGames = [
    { gameId: 2022090800, home: 'BUF', away: 'LAR', week: 1 },
    { gameId: 2022091101, home: 'KC', away: 'ARI', week: 1 },
    { gameId: 2022091108, home: 'DAL', away: 'TB', week: 1 }
  ];

  const routes = ['Go', 'Slant', 'Out', 'Corner', 'Post', 'Curl'];
  const receivers = ['T.Hill', 'S.Diggs', 'J.Jefferson', 'D.Adams', 'C.Kupp'];
  const allPlaysData = [];

  sampleGames.forEach(game => {
    for (let playNum = 1; playNum <= 8; playNum++) {
      const route = routes[Math.floor(Math.random() * routes.length)];
      const receiver = receivers[Math.floor(Math.random() * receivers.length)];
      const frames = generatePlayFrames(route);

      allPlaysData.push({
        gameId: game.gameId,
        playId: playNum,
        homeTeam: game.home,
        awayTeam: game.away,
        description: `${receiver} - ${route} Route`,
        route: route,
        receiver: receiver,
        passResult: Math.random() > 0.3 ? 'C' : 'I',
        frames: frames
      });
    }
  });

  setGames(sampleGames);
  setAllPlays(allPlaysData);
  setSelectedGame(sampleGames[0].gameId);
  setFilteredPlays(allPlaysData.filter(p => p.gameId === sampleGames[0].gameId));
};

const generatePlayFrames = (routeType) => {
  const frames = [];
  const numFrames = 45;

  // Generate different route patterns
  for (let i = 0; i < numFrames; i++) {
    const t = i / numFrames;

    // Wide Receiver route based on type
    let wrX = 30, wrY = 26.65;
    switch(routeType) {
      case 'Go':
        wrX = 30 + Math.sin(t * 0.5) * 2;
        wrY = 26.65 + t * 25;
        break;
      case 'Slant':
        wrX = 30 + t * 15;
        wrY = 26.65 + t * 12;
        break;
      case 'Out':
        wrX = 30 + (t > 0.5 ? (t - 0.5) * 30 : 0);
        wrY = 26.65 + Math.min(t * 15, 10);
        break;
      case 'Corner':
        wrX = 30 + (t > 0.6 ? (t - 0.6) * 20 : 0);
        wrY = 26.65 + t * 20;
        break;
      case 'Post':
        wrX = 30 + (t > 0.5 ? -(t - 0.5) * 15 : 0);
        wrY = 26.65 + t * 22;
        break;
      default: // Curl
        wrX = 30;
        wrY = 26.65 + (t < 0.7 ? t * 15 : 10.5 - (t - 0.7) * 5);
    }

    // Defender (following but slower)
    const defX = wrX + Math.cos(t * 2) * 1.5 - 1;
    const defY = wrY - 1.5 - Math.sin(t * 3) * 0.5;

    // Additional players (offense)
    const qb = { x: 30, y: 15, role: 'QB', team: 'offense' };
    const wr2 = { x: 60 + Math.sin(t * 2) * 3, y: 26.65 + t * 18, role: 'WR', team: 'offense' };
    const ol1 = { x: 28, y: 18, role: 'OL', team: 'offense' };
    const ol2 = { x: 30, y: 18, role: 'OL', team: 'offense' };
    const ol3 = { x: 32, y: 18, role: 'OL', team: 'offense' };

    // Additional defenders
    const def2 = { x: 62, y: wrY - 2, role: 'DB', team: 'defense' };
    const lb1 = { x: 28, y: 22 + t * 3, role: 'LB', team: 'defense' };
    const lb2 = { x: 32, y: 22 + t * 3, role: 'LB', team: 'defense' };

    const wr = { x: wrX, y: wrY, role: 'WR_TARGET', team: 'offense', name: 'WR' };
    const def = { x: defX, y: defY, role: 'DB_PRIMARY', team: 'defense', name: 'DB' };

    // Ball (thrown at frame 12)
    const ball = i < 12 ? null : {
      x: qb.x + (wrX - qb.x) * ((i - 12) / (numFrames - 12)),
      y: qb.y + (wrY - qb.y) * ((i - 12) / (numFrames - 12)),
      role: 'ball'
    };

    const separation = Math.sqrt(Math.pow(wrX - defX, 2) + Math.pow(wrY - defY, 2));

    frames.push({
      frameId: i,
      players: [wr, def, qb, wr2, ol1, ol2, ol3, def2, lb1, lb2],
      ball: ball,
      separation: separation
    });
  }

  return frames;
};

useEffect(() => {
  generateSampleData();
}, []);

useEffect(() => {
  if (selectedGame) {
    const plays = allPlays.filter(p => p.gameId === selectedGame);
    setFilteredPlays(plays);
    setCurrentPlayIndex(0);
    setFrame(0);
    setIsPlaying(false);
  }
}, [selectedGame, allPlays]);

useEffect(() => {
  if (!isPlaying || filteredPlays.length === 0) return;

  const currentPlay = filteredPlays[currentPlayIndex];
  if (!currentPlay) return;

  const interval = setInterval(() => {
    setFrame(prev => {
      if (prev >= currentPlay.frames.length - 1) {
        setIsPlaying(false);
        return prev;
      }
      return prev + 1;
    });
  }, playSpeed);

  return () => clearInterval(interval);
}, [isPlaying, currentPlayIndex, filteredPlays, playSpeed]);

useEffect(() => {
  if (filteredPlays.length === 0 || !canvasRef.current) return;

  const currentPlay = filteredPlays[currentPlayIndex];
  if (!currentPlay) return;

  const canvas = canvasRef.current;
  const ctx = canvas.getContext('2d');
  const width = canvas.width;
  const height = canvas.height;

  // Clear and draw field
  ctx.fillStyle = '#1a472a';
  ctx.fillRect(0, 0, width, height);

  // Draw yard lines
  ctx.strokeStyle = 'rgba(255, 255, 255, 0.15)';
  ctx.lineWidth = 1;
  for (let i = 0; i <= 10; i++) {
    const y = (i / 10) * height;
    ctx.beginPath();
    ctx.moveTo(0, y);
    ctx.lineTo(width, y);
    ctx.stroke();
  }

  // Draw hash marks
  ctx.strokeStyle = 'rgba(255, 255, 255, 0.1)';
  [width * 0.4, width * 0.6].forEach(x => {
    ctx.beginPath();
    ctx.moveTo(x, 0);
    ctx.lineTo(x, height);
    ctx.stroke();
  });

  const currentFrame = currentPlay.frames[frame];
  const initialSep = currentPlay.frames[0].separation;
  const sepGain = currentFrame.separation - initialSep;

  // Draw players
  currentFrame.players.forEach(player => {
    const x = (player.x / 120) * width;
    const y = (player.y / 53.3) * height;

    let fillColor, strokeColor, size;

    if (player.role === 'WR_TARGET') {
      fillColor = '#3b82f6';
      strokeColor = '#60a5fa';
      size = 10;
    } else if (player.role === 'DB_PRIMARY') {
      fillColor = '#ef4444';
      strokeColor = '#f87171';
      size = 10;
    } else if (player.team === 'offense') {
      fillColor = '#60a5fa';
      strokeColor = '#93c5fd';
      size = 7;
    } else {
      fillColor = '#f87171';
      strokeColor = '#fca5a5';
      size = 7;
    }

    ctx.fillStyle = fillColor;
    ctx.strokeStyle = strokeColor;
    ctx.lineWidth = 2;
    ctx.beginPath();
    ctx.arc(x, y, size, 0, Math.PI * 2);
    ctx.fill();
    ctx.stroke();

    // Label primary players
    if (player.role === 'WR_TARGET' || player.role === 'DB_PRIMARY' || player.role === 'QB') {
      ctx.fillStyle = 'white';
      ctx.font = 'bold 8px sans-serif';
      ctx.textAlign = 'center';
      ctx.fillText(player.role.split('_')[0], x, y - 14);
    }
  });

  // Draw ball
  if (currentFrame.ball) {
    const x = (currentFrame.ball.x / 120) * width;
    const y = (currentFrame.ball.y / 53.3) * height;

    ctx.fillStyle = '#fbbf24';
    ctx.strokeStyle = '#f59e0b';
    ctx.lineWidth = 2;
    ctx.beginPath();
    ctx.arc(x, y, 5, 0, Math.PI * 2);
    ctx.fill();
    ctx.stroke();
  }

  // Draw separation line
  const wr = currentFrame.players.find(p => p.role === 'WR_TARGET');
  const def = currentFrame.players.find(p => p.role === 'DB_PRIMARY');

  if (wr && def) {
    const x1 = (wr.x / 120) * width;
    const y1 = (wr.y / 53.3) * height;
    const x2 = (def.x / 120) * width;
    const y2 = (def.y / 53.3) * height;

    ctx.strokeStyle = 'rgba(255, 255, 255, 0.6)';
    ctx.lineWidth = 1.5;
    ctx.setLineDash([3, 3]);
    ctx.beginPath();
    ctx.moveTo(x1, y1);
    ctx.lineTo(x2, y2);
    ctx.stroke();
    ctx.setLineDash([]);
  }

  // Draw metrics overlay on canvas
  ctx.fillStyle = 'rgba(0, 0, 0, 0.75)';
  ctx.fillRect(10, 10, 140, 95);

  ctx.fillStyle = 'white';
  ctx.font = 'bold 11px sans-serif';
  ctx.textAlign = 'left';
  ctx.fillText('SEPARATION', 20, 28);

  ctx.font = 'bold 20px sans-serif';
  ctx.fillStyle = '#3b82f6';
  ctx.fillText(`${currentFrame.separation.toFixed(1)} YDS`, 20, 52);

  ctx.font = '10px sans-serif';
  ctx.fillStyle = sepGain >= 0 ? '#10b981' : '#ef4444';
  ctx.fillText(`${sepGain >= 0 ? '+' : ''}${sepGain.toFixed(1)} gain`, 20, 70);

  ctx.fillStyle = '#9ca3af';
  ctx.font = '9px sans-serif';
  ctx.fillText(`Frame ${frame + 1}/${currentPlay.frames.length}`, 20, 88);
  ctx.fillText(`${(frame / 10).toFixed(1)}s`, 20, 100);

}, [frame, currentPlayIndex, filteredPlays]);

const handlePlayPause = () => setIsPlaying(!isPlaying);
const handleReset = () => { setFrame(0); setIsPlaying(false); };
const handlePrevPlay = () => {
  if (currentPlayIndex > 0) {
    setCurrentPlayIndex(currentPlayIndex - 1);
    setFrame(0);
    setIsPlaying(false);
  }
};
const handleNextPlay = () => {
  if (currentPlayIndex < filteredPlays.length - 1) {
    setCurrentPlayIndex(currentPlayIndex + 1);
    setFrame(0);
    setIsPlaying(false);
  }
};

const handleFileUpload = (e) => {
  alert('Connect this to parse your parquet files with game_id, play_id, frame_id, x, y, player_role data');
};

if (filteredPlays.length === 0) {
  return <div className="flex items-center justify-center h-screen bg-gray-900 text-white">Loading...</div>;
}

const currentPlay = filteredPlays[currentPlayIndex];
const game = games.find(g => g.gameId === selectedGame);

return (
  <div className="w-full min-h-screen bg-gray-900 text-white p-6">
    <div className="max-w-5xl mx-auto">
      {/* Header */}
      <div className="mb-4">
        <h1 className="text-2xl font-bold mb-1">NFL Play-by-Play Analyzer</h1>
        <p className="text-sm text-gray-400">Separation Tracking & Route Visualization</p>
      </div>

      {/* Game Selector */}
      <div className="mb-4 flex gap-4 items-center">
        <div className="flex-1">
          <label className="block text-sm font-medium mb-2">Select Game</label>
          <select
            value={selectedGame || ''}
            onChange={(e) => setSelectedGame(parseInt(e.target.value))}
            className="w-full bg-gray-800 border border-gray-700 rounded-lg px-4 py-2 text-white"
          >
            {games.map(game => (
              <option key={game.gameId} value={game.gameId}>
                Week {game.week}: {game.away} @ {game.home}
              </option>
            ))}
          </select>
        </div>

      </div>

      {/* Play Selector */}
      <div className="mb-4 bg-gray-800 rounded-lg p-4">
        <div className="flex items-center justify-between mb-2">
          <h3 className="text-sm font-semibold flex items-center gap-2">
            <Filter className="w-4 h-4" />
            Play {currentPlayIndex + 1} of {filteredPlays.length}
          </h3>
          <div className="flex gap-2">
            <button
              onClick={handlePrevPlay}
              disabled={currentPlayIndex === 0}
              className="p-2 bg-gray-700 hover:bg-gray-600 rounded disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <SkipBack className="w-4 h-4" />
            </button>
            <button
              onClick={handleNextPlay}
              disabled={currentPlayIndex === filteredPlays.length - 1}
              className="p-2 bg-gray-700 hover:bg-gray-600 rounded disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <SkipForward className="w-4 h-4" />
            </button>
          </div>
        </div>
        <div className="text-lg font-semibold">{currentPlay.description}</div>
        <div className="text-sm text-gray-400">
          Game {currentPlay.gameId} • Play {currentPlay.playId} • Result: {currentPlay.passResult === 'C' ? 'Complete' : 'Incomplete'}
        </div>
      </div>

      {/* Animation Canvas - Compact size for reports */}
      <div className="bg-gray-800 rounded-lg p-4 mb-4">
        <div className="relative">
          <canvas
            ref={canvasRef}
            width={700}
            height={380}
            className="w-full rounded-lg shadow-xl"
          />
        </div>

        {/* Controls */}
        <div className="mt-4 space-y-3">
          <div className="flex items-center gap-2">
            <button
              onClick={handleReset}
              className="p-2 bg-gray-700 hover:bg-gray-600 rounded-lg"
              title="Reset"
            >
              <SkipBack className="w-4 h-4" />
            </button>
            <button
              onClick={handlePlayPause}
              className="p-2 bg-blue-600 hover:bg-blue-500 rounded-lg"
              title={isPlaying ? 'Pause' : 'Play'}
            >
              {isPlaying ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
            </button>
            <input
              type="range"
              min="0"
              max={currentPlay.frames.length - 1}
              value={frame}
              onChange={(e) => setFrame(parseInt(e.target.value))}
              className="flex-1"
            />
            <div className="text-xs text-gray-400 w-24 text-right">
              {(frame / 10).toFixed(1)}s
            </div>
          </div>

          <div className="flex items-center gap-3">
            <label className="text-xs text-gray-400">Speed:</label>
            <input
              type="range"
              min="50"
              max="300"
              step="50"
              value={playSpeed}
              onChange={(e) => setPlaySpeed(parseInt(e.target.value))}
              className="flex-1"
            />
            <span className="text-xs text-gray-400 w-16">{(1000/playSpeed).toFixed(1)}x</span>
          </div>
        </div>
      </div>

      {/* Legend - Compact */}
      <div className="bg-gray-800 rounded-lg p-4">
        <div className="grid grid-cols-2 gap-3 text-xs">
          <div className="flex items-center gap-2">
            <div className="w-5 h-5 bg-blue-500 rounded-full border-2 border-blue-300"></div>
            <span>Targeted WR</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-5 h-5 bg-red-500 rounded-full border-2 border-red-300"></div>
            <span>Primary DB</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 bg-blue-400 rounded-full"></div>
            <span>Offense</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 bg-red-400 rounded-full"></div>
            <span>Defense</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-5 h-5 bg-yellow-400 rounded-full border-2 border-yellow-600"></div>
            <span>Football</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-5 h-0.5 bg-white opacity-60"></div>
            <span>Separation</span>
          </div>
        </div>
      </div>
    </div>
  </div>
);
};

export default NFLPlayVisualizer;
