import React, { useEffect, useState, useMemo, useRef } from 'react';

// --- Utility Functions ---
const normalizeName = (name) => {
  if (!name) return '';
  return name
    .toLowerCase()
    .replace(/\./g, '')        
    .replace(/[^a-z\s-]/g, '') 
    .replace(/\s+/g, '-');     
};

const useTransitionEffect = (targetValue, duration = 500) => {
  const [currentValue, setCurrentValue] = useState(targetValue);
  const animationRef = useRef();

  useEffect(() => {
    if (isNaN(targetValue)) {
      setCurrentValue(targetValue);
      return;
    }

    cancelAnimationFrame(animationRef.current);
    const startValue = currentValue;
    const startTime = performance.now();

    const animate = (time) => {
      const elapsed = time - startTime;
      const progress = Math.min(1, elapsed / duration);
      const easedProgress = progress < 0.5 
        ? 2 * progress * progress 
        : -1 + (4 - 2 * progress) * progress;
      const newValue = startValue + (targetValue - startValue) * easedProgress;
      setCurrentValue(newValue);
      if (progress < 1) {
        animationRef.current = requestAnimationFrame(animate);
      }
    };

    animationRef.current = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationRef.current);
  }, [targetValue, duration]);

  return currentValue;
};

// --- Player Node Component ---
const PlayerNode = React.memo(({ d, xPos, yPos, hovered, setHovered, colorMode, dataRanges }) => {
  const cleanId = `clip-${normalizeName(d.player)}`;
  const initials = d.player.split(' ').map(n => n[0]).join('');

  const animatedRadius = useTransitionEffect(d.radius);
  const animatedDiameter = animatedRadius * 2;

  // Dynamic color based on mode
  const getColor = () => {
    if (colorMode === 'default') {
      return d.wos > d.exp ? '#10b981' : '#ef4444';
    }
    if (colorMode === 'def_speed') {
      // Normalize defender speed to 0-1 range
      const { defMin, defMax } = dataRanges;
      const normalized = (d.def_avg_speed - defMin) / (defMax - defMin);
      // Green (slow defenders = easy) -> Red (fast defenders = hard)
      const hue = 120 - normalized * 120; // 120 (green) to 0 (red)
      return `hsl(${hue}, 75%, 50%)`;
    }
    return '#888';
  };

  const strokeColor = getColor();

  return (
    <g 
      onMouseEnter={() => setHovered(d)}
      onMouseLeave={() => setHovered(null)}
      className="cursor-pointer"
      style={{ 
        opacity: hovered ? (hovered.player !== d.player ? 0.25 : 1) : 1,
        transition: 'opacity 0.3s ease'
      }}
    >
      <defs>
        <clipPath id={cleanId}>
          <circle cx={xPos} cy={yPos} r={animatedRadius} />
        </clipPath>
      </defs>

      <circle cx={xPos} cy={yPos} r={animatedRadius} fill="#b8b8b8" />
      <text 
        x={xPos} y={yPos + 4} textAnchor="middle" 
        fontSize={animatedRadius * 0.8} fontWeight="bold" fill="#444" 
        style={{ pointerEvents: 'none' }} 
      >
        {initials}
      </text>

      <image 
        xlinkHref={d.img} 
        x={xPos - animatedRadius} 
        y={yPos - animatedRadius} 
        width={animatedDiameter} 
        height={animatedDiameter} 
        preserveAspectRatio="xMidYMid slice"
        clipPath={`url(#${cleanId})`}
        onError={(e) => { e.target.style.display = 'none'; }}
      />

      <circle 
        cx={xPos} cy={yPos} r={animatedRadius} fill="none" 
        stroke={strokeColor} 
        strokeWidth={3}
        filter="url(#dropShadow)"
        style={{ transition: 'stroke 0.8s ease-in-out' }} 
      />

      <text
        x={xPos}
        y={yPos + animatedRadius + 12} 
        textAnchor="middle"
        fontSize="10" 
        fontWeight="600"
        fill="#333"
        className="pointer-events-none"
      >
        {d.player}
      </text>
    </g>
  );
});


const RouteComparisonVisualizer = () => {
  const [data, setData] = useState([]);
  const [hovered, setHovered] = useState(null);
  const [minTargets, setMinTargets] = useState(50);
  const [colorMode, setColorMode] = useState('default'); // 'default' or 'def_speed'
  const [isAnimating, setIsAnimating] = useState(false);
  const [dataRanges, setDataRanges] = useState({ defMin: 0, defMax: 2 });

  useEffect(() => {
    fetch('/data/elite_leaderboard3.csv')
      .then(res => res.text())
      .then(text => {
        const [header, ...rows] = text.trim().split('\n');
        const cols = header.split(',');

        const parsed = rows.map(r => {
          const v = r.split(',');
          const row = {};
          cols.forEach((c, i) => (row[c] = v[i]));
          if (!row.player_name) return null;

          return {
            player: row.player_name,
            wos: +row.Advanced_WOS,
            exp: +row.exp_sep_xgb,
            targets: +row.play_id,
            sep_at_arrival: +row.sep_at_arrival,
            pass_length: +row.pass_length,
            time_to_throw: +row.time_to_throw,
            def_avg_speed: +row.def_avg_speed,
            img: `/headshots/${normalizeName(row.player_name)}.jpg`
          };
        });

        const validData = parsed.filter(d => d && !isNaN(d.wos) && !isNaN(d.exp));

        // Calculate ranges for color scaling
        const allDefSpeed = validData.map(d => d.def_avg_speed);
        setDataRanges({
          defMin: Math.min(...allDefSpeed),
          defMax: Math.max(...allDefSpeed)
        });

        setData(validData);
      });
  }, []);

  // Animation sequence
  const runAnimation = () => {
    setIsAnimating(true);
    setColorMode('default');

    // After 2 seconds, switch to defender speed coloring
    setTimeout(() => {
      setColorMode('def_speed');
    }, 1500);

    // After 8 seconds, return to default
    setTimeout(() => {
      setColorMode('default');
      setIsAnimating(false);
    }, 8000);
  };

  const processedData = useMemo(() => {
    if (!data.length) return [];
    let filtered = data.filter(d => d.targets >= minTargets);
    return filtered.map(d => ({
      ...d,
      radius: 18
    }));
  }, [data, minTargets]);

  if (!data.length) return <div className="p-10 text-center font-serif text-gray-500">Loading Chart...</div>;

  const W = 1400; 
  const H = 800;  
  const P = 100; 

  const xs = data.map(d => d.wos);
  const ys = data.map(d => d.exp);
  const xMin = Math.min(...xs) - 0.1;
  const xMax = Math.max(...xs) + 0.1;
  const yMin = Math.min(...ys) - 0.1;
  const yMax = Math.max(...ys) + 0.1;
  const avgX = xs.reduce((a, b) => a + b, 0) / xs.length;
  const avgY = ys.reduce((a, b) => a + b, 0) / ys.length;

  const xScale = v => P + ((v - xMin) / (xMax - xMin)) * (W - P * 2);
  const yScale = v => H - P - ((v - yMin) / (yMax - yMin)) * (H - P * 2);

  return (
    <div className="flex flex-col items-center bg-[#f4f1ea] p-8 rounded-xl shadow-2xl max-w-[1500px] mx-auto font-serif">

      {/* Header */}
      <div className="mb-4 text-center">
        <h2 className="text-3xl font-bold text-[#2c2c2c] uppercase tracking-wider">
          Phase 3: Defender Speed â†’ Expected Separation
        </h2>
        <p className="text-[#666] italic mt-1">
          Faster defenders close gaps quicker, reducing expected separation
        </p>
      </div>

      {/* Animation Control */}
      <button
        onClick={runAnimation}
        disabled={isAnimating}
        className={`mb-6 px-8 py-3 rounded-full font-bold text-lg uppercase tracking-wider transition-all
                    ${isAnimating 
                      ? 'bg-gray-400 text-gray-600 cursor-not-allowed' 
                      : 'bg-gradient-to-r from-green-500 to-red-500 text-white hover:scale-105 shadow-lg'}`}
      >
        {isAnimating ? 'Playing...' : 'â–¶ Show Defender Speed Impact'}
      </button>

      {/* Legend */}
      <div className={`mb-4 flex items-center gap-4 transition-all duration-500 ${colorMode === 'def_speed' ? 'opacity-100' : 'opacity-0'}`}>
        <span className="text-sm font-semibold text-gray-600">Avg Defender Speed:</span>
        <div className="flex items-center gap-1">
          <div className="w-6 h-6 rounded-full" style={{ backgroundColor: 'hsl(120, 75%, 50%)' }} />
          <span className="text-xs text-gray-500">Slow ({dataRanges.defMin.toFixed(2)} mph)</span>
        </div>
        <div className="w-32 h-3 rounded-full" style={{ background: 'linear-gradient(to right, hsl(120, 75%, 50%), hsl(60, 75%, 50%), hsl(0, 75%, 50%))' }} />
        <div className="flex items-center gap-1">
          <div className="w-6 h-6 rounded-full" style={{ backgroundColor: 'hsl(0, 75%, 50%)' }} />
          <span className="text-xs text-gray-500">Fast ({dataRanges.defMax.toFixed(2)} mph)</span>
        </div>
      </div>

      {/* Insight Text */}
      <div className={`mb-4 px-6 py-3 bg-red-50 border border-red-200 rounded-lg transition-all duration-500 ${colorMode === 'def_speed' ? 'opacity-100' : 'opacity-0'}`}>
        <p className="text-red-800 font-medium text-center">
          ðŸ’¡ Notice: Players facing <span className="text-red-600 font-bold">faster defenders</span> tend to have 
          <span className="font-bold"> lower expected separation</span> (lower on Y-axis)
        </p>
      </div>

      {/* SVG Chart */}
      <div className="relative">
        <svg width={W} height={H} className="border border-[#dcd9d0] bg-[#f9f7f1]">
          <defs>
            <filter id="dropShadow" x="-20%" y="-20%" width="140%" height="140%">
              <feDropShadow dx="2" dy="2" stdDeviation="2" floodColor="#000" floodOpacity="0.25" />
            </filter>
          </defs>

          {/* Axis Labels */}
          <text x={W/2} y={H - 30} textAnchor="middle" fontWeight="bold" fill="#333" fontSize="16">
            Created Separation (WOS) â†’
          </text>
          <text x={30} y={H/2} textAnchor="middle" fontWeight="bold" fill="#333" fontSize="16" transform={`rotate(-90, 30, ${H/2})`}>
            Expected Separation â†’
          </text>

          {/* Grid lines */}
          <line x1={xScale(avgX)} y1={P} x2={xScale(avgX)} y2={H-P} stroke="#ccc" strokeWidth="1" strokeDasharray="4,4" />
          <line x1={P} y1={yScale(avgY)} x2={W-P} y2={yScale(avgY)} stroke="#ccc" strokeWidth="1" strokeDasharray="4,4" />

          {/* Axis ticks */}
          {[xMin, avgX, xMax].map((v,i) => (
            <text key={i} x={xScale(v)} y={H-P+20} textAnchor="middle" fill="#666" fontSize="12">{v.toFixed(2)}</text>
          ))}
          {[yMin, avgY, yMax].map((v,i) => (
            <text key={i} x={P-10} y={yScale(v)} textAnchor="end" fill="#666" fontSize="12" dy="0.3em">{v.toFixed(2)}</text>
          ))}

          {/* Player Nodes */}
          {processedData.map(d => (
            <PlayerNode
              key={d.player}
              d={d}
              xPos={xScale(d.wos)}
              yPos={yScale(d.exp)}
              hovered={hovered}
              setHovered={setHovered}
              colorMode={colorMode}
              dataRanges={dataRanges}
            />
          ))}
        </svg>

        {/* Tooltip */}
        {hovered && (
          <div 
            className="absolute z-50 bg-white/95 backdrop-blur border border-stone-300 shadow-xl p-4 rounded min-w-[220px] pointer-events-none"
            style={{ left: xScale(hovered.wos) + 25, top: yScale(hovered.exp) - 30 }}
          >
            <div className="font-bold text-lg text-stone-800 border-b pb-1 mb-2">{hovered.player}</div>
            <div className="grid grid-cols-2 gap-x-3 text-sm">
              <span className="text-gray-500">Expected Sep:</span> 
              <span className="text-right font-bold">{hovered.exp.toFixed(2)}</span>
              <span className="text-gray-500">Created Sep:</span> 
              <span className="text-right font-bold text-emerald-600">{hovered.wos.toFixed(2)}</span>
              <span className="text-red-600 font-semibold">Def Avg Speed:</span> 
              <span className="text-right font-bold text-red-600">{hovered.def_avg_speed.toFixed(2)} mph</span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RouteComparisonVisualizer;
